import * as api from "@/api/data-base/source-list";
import orgSelect from "@/components/Select/orgSelect.vue";
import Pagination from "@/components/Pagination";
import { DICT_CODE, TWO_STATE_OPTIONS, DT_ORG_TYPE } from "@/utils/constant";
export default {
  name: "wlxz",
  components: {
    Pagination: Pagination,
    orgSelect: orgSelect
  },
  props: ['supplierCode', 'orgCode', 'materielselect', 'unMutiple', 'toOrgCode'],
  data: function data() {
    return {
      list: null,
      listSelected: [],
      total: 0,
      parentsRows: null,
      listQuery: {
        page: true,
        currentPage: 1,
        pageSize: 10,
        materielCode: undefined,
        supplierCode: undefined,
        orgCode: undefined,
        toOrgCode: undefined
      },
      selectCode: [],
      tableKey: 0,
      activeName2: 'first',
      DICT_CODE: DICT_CODE,
      TWO_STATE_OPTIONS: TWO_STATE_OPTIONS,
      DT_ORG_TYPE: DT_ORG_TYPE
    };
  },
  methods: {
    checked: function checked(i) {
      this.$refs.tbselect.toggleRowSelection(this.list[i], true);
    },
    getList: function getList() {
      var _this = this;

      api.queryRecords(this.listQuery).then(function (res) {
        _this.list = res.data.list;
        _this.total = res.data.pages.count;

        if (_this.listSelected) {
          _this.listSelected.forEach(function (items) {
            items.snp2 = items.snp;

            _this.list.forEach(function (item, index) {
              if (item.materielCode === items.materielCode) {
                _this.$nextTick(function () {
                  this.checked(index);
                });
              }
            });
          });
        }
      });
    },
    handleQuery: function handleQuery() {
      this.listQuery.currentPage = 1;
      this.getList();
    },
    handleSizeChange: function handleSizeChange(val) {
      this.listQuery.pageSize = val;
      this.getList();
    },
    handleCurrentChange: function handleCurrentChange(val) {
      this.listQuery.currentPage = val;
      this.getList();
    },
    //检查是否取消勾选
    checkUnsel: function checkUnsel(val, row) {
      var isuncheck = true;
      val.forEach(function (item) {
        if (item.materielCode === row.materielCode) {
          isuncheck = false;
        }
      });
      return isuncheck;
    },
    selectMain: function selectMain(val, row) {
      var _this2 = this;

      if (this.unMutiple) {
        var isuncheck = this.checkUnsel(val, row);

        if (!isuncheck) {
          var arr = [];
          arr.push(val[val.length - 1]);
          this.listSelected = arr;

          if (val.length > 1) {
            this.$refs.tbselect.clearSelection();
            this.$refs.tbselect.toggleRowSelection(val[val.length - 1], 'selected');
          }
        } else {
          this.listSelected = [];
        }
      } else {
        var _isuncheck = this.checkUnsel(val, row);

        if (!_isuncheck) {
          if (this.listSelected) {
            val.forEach(function (items, indexv) {
              var isSelected = false;

              _this2.listSelected.forEach(function (item) {
                if (item.materielCode === items.materielCode) {
                  isSelected = true;
                }
              });

              if (!isSelected) {
                _this2.listSelected.push(val[indexv]);
              }
            });
          } else {
            this.listSelected = val;
          }
        } else {
          this.listSelected = this.listSelected.filter(function (item) {
            return item.materielCode !== row.materielCode;
          });
        }
      }
    }
  },
  watch: {
    supplierCode: {
      handler: function handler(newValue) {
        this.listQuery.supplierCode = newValue;
        this.getList();
      },
      deep: true
    },
    orgCode: {
      handler: function handler(newValue) {
        this.listQuery.orgCode = newValue;
        this.getList();
      },
      deep: true
    },
    toOrgCode: {
      handler: function handler(newValue) {
        this.listQuery.toOrgCode = newValue;
        this.getList();
      },
      deep: true
    }
  }
};
