import request from '@/utils/request'; //采购配额调整查询

export function queryRecords(query) {
  return request({
    url: "/purchase/adjust",
    method: 'get',
    params: query
  });
} //采购配额调整明细查询

export function queryRecordsItem(query) {
  return request({
    url: "/purchase/adjust/find",
    method: 'get',
    params: query
  });
} //采购配额调整物料查询

export function querymaterialRecords(query) {
  return request({
    url: "/purchase/quotaAdjustment/materialSelection",
    method: 'get',
    params: query
  });
} //采购配额调整（新增）

export function addRecord(data) {
  return request({
    url: "/purchase/adjust",
    method: 'post',
    data: data
  });
} //采购配额调整（编辑）

export function updateRecord(rowId, data) {
  return request({
    url: "/purchase/adjust/".concat(rowId),
    method: 'patch',
    data: data
  });
} //采购配额调整申请删除

export function deleteRecord(rowId) {
  return request({
    url: "/purchase/adjust/".concat(rowId),
    method: 'delete'
  });
} //采购配额调整申请（审核）

export function examineRecord(data) {
  return request({
    url: "/purchase/adjust/commit",
    method: 'patch',
    data: data
  });
} //采购配额调整申请（提交）

export function submit(data) {
  return request({
    url: "/purchase/quotaAdjustment/commit",
    method: 'patch',
    data: data
  });
}
