import request from '@/utils/request'; //采购配额设置查询

export function queryRecords(query) {
  return request({
    url: "/business/purchase-route/route",
    method: 'get',
    params: query
  });
} //采购配额查询

export function queryRecord(query) {
  return request({
    url: "/purchase/quotaSet",
    method: 'get',
    params: query
  });
} //采购配额设置（生成）

export function addRecord(data) {
  return request({
    url: "/purchase/quotaSet",
    method: 'post',
    data: data
  });
} //采购配额设置（全生成）

export function alladdRecord(data) {
  return request({
    url: "/purchase/quotaSet/allcreate",
    method: 'post',
    data: data
  });
} //采购配额调整（编辑）

export function updateRecord(rowId, data) {
  return request({
    url: "/purchase/adjust/".concat(rowId),
    method: 'patch',
    data: data
  });
} //采购配额调整申请删除

export function deleteRecord(rowId) {
  return request({
    url: "/purchase/adjust/".concat(rowId),
    method: 'delete'
  });
} //采购配额调整申请（审核）

export function examineRecord(data) {
  return request({
    url: "/purchase/adjust/commit",
    method: 'patch',
    data: data
  });
} //采购配额调整申请（提交）

export function submit(data) {
  return request({
    url: "/purchase/quotaAdjustment/commit",
    method: 'patch',
    data: data
  });
}
