import * as api from "@/api/project/srm/priceManage/detailQuoteNote";
export default {
  name: 'addMain',
  props: ['unMutiple', 'title', 'curdialogStatus'],
  data: function data() {
    return {
      formData: {
        noticeNo: null,
        materielCode: null,
        materielName: null,
        latestQuote: null,
        supplierCode: null,
        supplierName: null,
        tableDetails: []
      },
      // tableDetails: [],
      listSelected: null,
      //已选项
      selectRowData: null,
      dialogVisible: false,
      formConfig: [{
        type: 'commonInput',
        label: '报价通知单号',
        prop: 'noticeNo',
        width: ''
      }, {
        type: 'commonInput',
        label: '物料编码',
        prop: 'materielCode',
        width: ''
      }, {
        type: 'commonInput',
        label: '物料名称',
        prop: 'materielName',
        width: ''
      }, {
        type: 'commonInput',
        label: '当前报价',
        prop: 'totalPrice',
        width: ''
      }, {
        type: 'commonInput',
        label: '供应商编码',
        prop: 'supplierCode',
        width: ''
      }, {
        type: 'commonInput',
        label: '供应商名称',
        prop: 'supplierName',
        width: ''
      }],
      tableConfig: [{
        label: '序号',
        prop: 'number',
        align: 'center',
        width: 'auto'
      }, {
        label: '报价轮次',
        prop: 'quoteTimes',
        align: 'center',
        width: 'auto'
      }, {
        label: '价格',
        prop: 'price',
        align: 'center',
        width: 'auto'
      }, {
        label: '报价时间',
        prop: 'quoteTime',
        align: 'center',
        width: 'auto'
      }, {
        label: '报价方式',
        prop: 'quoteTypeName',
        align: 'center',
        width: 'auto'
      }],
      row: null
    };
  },
  methods: {
    //表格操作预览
    handlePreview: function handlePreview(index, row) {
      console.log('预览', index, row);
    },
    setFormData: function setFormData(row) {
      this.row = row;

      for (var key in this.formData) {
        for (var par in row) {
          if (key === par) {
            this.$set(this.formData, key, row[key]);
          }
        }
      }

      this.formData.totalPrice = row.totalPrice;
      this.$set(this.formData, 'validDateEnd', undefined);
    },
    resetTemp: function resetTemp() {
      this.formData = {
        validDateEnd: undefined
      };
    },
    //确认报价
    handleConfirmQuote: function handleConfirmQuote() {
      var _this = this;

      this.$confirm('确认要报价该数据项吗?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function () {
        var dto = {
          method: 'confirm',
          validDateEnd: _this.formData.validDateEnd,
          rowIds: [_this.row.rowId]
        };
        api.confirmRecords(dto).then(function () {
          _this.$message({
            title: '成功',
            message: '确认报价成功',
            type: 'success',
            duration: 2000
          });

          _this.dialogVisible = false;

          _this.$emit('refresh');
        });
      }).catch(function () {
        _this.$message({
          type: 'info',
          message: '已取消'
        });
      });
    },
    //二次报价
    handleSecondQuote: function handleSecondQuote() {
      var _this2 = this;

      if (this.formData.validDateEnd) {
        this.$confirm('确认要二次报价该数据项吗?', '提示', {
          confirmButtonText: '确定',
          cancelButtonText: '取消',
          type: 'warning'
        }).then(function () {
          var dto = {
            method: 'requote',
            validDateEnd: _this2.formData.validDateEnd,
            rowIds: [_this2.row.rowId]
          };
          api.confirmRecords(dto).then(function () {
            _this2.$message({
              title: '成功',
              message: '二次报价成功',
              type: 'success',
              duration: 2000
            });

            _this2.dialogVisible = false;

            _this2.$emit('refresh');
          });
        }).catch(function () {
          _this2.$message({
            type: 'info',
            message: '已取消'
          });
        });
      } else {
        this.$message({
          type: 'warning',
          message: '请先填写报价截止日期'
        });
      }
    },
    selectChange: function selectChange(val) {
      if (this.unMutiple) {
        this.selectOneRow(val);
      } else {
        this.selectRows(val);
      }
    },
    selectRows: function selectRows(val) {
      this.selectRowData = val;
      this.listSelected = this.selectRowData; // this.getDetail(val[val.length - 1]);
      // this.$emit('clickRows' ,val,this.listSub);
    },
    selectOneRow: function selectOneRow(val) {
      var arr = [];
      arr.push(val[val.length - 1]);
      this.selectRowData = arr; // if (arr[0]) {
      //     this.getDetail(arr[0]);
      // }

      if (val.length > 1) {
        this.$refs.tbselect.clearSelection();
        this.$refs.tbselect.toggleRowSelection(val[val.length - 1], 'selected');
      }

      this.listSelected = this.selectRowData;
    }
  }
};
