import ckbm from "./ckbm";
import ckwlfp from "./ckwlfp";
import zsjwlxz from "@/components/Select/zsjwlxz.vue";
export default {
  name: "storageMaterialDistribution",
  components: {
    Pagination: Pagination,
    personSelect: personSelect,
    ckbm: ckbm,
    ImportFile: ImportFile,
    zsjwlxz: zsjwlxz,
