      this.theight = this.$myFun.getSingleTbHeight() + 52;
      this.stheight = this.theight;
    },
    getlistSub: function getlistSub() {
      var _this3 = this;

      // this.listQuerySub.rule = 'materiel'
      api.queryRecords(this.listQuerySub).then(function (res) {
        _this3.listSub = res.data.list;
        _this3.total1 = res.data.pages.count;
      });
    },
    selectRowMain: function selectRowMain(val) {
      //左表复选框选中方法
      this.selectRows = val[val.length - 1];

      if (val.length > 1) {
        this.$refs.tbmain.clearSelection();
        this.$refs.tbmain.toggleRowSelection(val[val.length - 1], 'selected');
      }

      if (val.length !== 0) {
        this.listQuerySub.warehouseCode = this.selectRows.warehouseCode;
        this.listQuerySub.areaCode = this.selectRows.areaCode;
        this.listQuerySub.locationCode = this.selectRows.locationCode;
        this.getlistSub();
      }
    },
    selectRow_line: function selectRow_line(val) {
      //左表单击行选中方法
      this.selectRows = val;
      this.$refs.tbmain.clearSelection();
      this.$refs.tbmain.toggleRowSelection(val, 'selected');
    },
    selectRowSub: function selectRowSub(val) {
      //右表复选框选中方法
      this.selectRowsSub = val; // console.log(this.selectRowsSub,11111)
      // if (val.length > 1) {
      //     this.$refs.mx.clearSelection();
      //     this.$refs.mx.toggleRowSelection(val[val.length-1],'selected');
      // }
    },
    handleDistribute: function handleDistribute() {
      if (this.listQuery.warehouseCode) {
        this.ckwlDialogFormVisible = true;
        this.warehouseCode = this.listQuery.warehouseCode;
      } else {
        this.$message({
          title: "警告",
          message: "请先选择仓库编码",
          type: "warning"
        });
      }
    },
    //主表仓库选择设置
    handleClickckbm: function handleClickckbm() {
      this.ckbmDialogFormVisible = true;
    },
    //仓库编码确认
    confirmCkbm: function confirmCkbm(row) {
      this.ckbmRows = row;
    },
    confirmMateriel: function confirmMateriel() {
      var _this4 = this;

      //新增物料确认按钮
      if (this.$refs.wlxzRef.listSelected.length > 0) {
        var dtos = [];
        var wlList = this.$refs.wlxzRef.listSelected;
        wlList.forEach(function (item) {
          var obj = {
            areaCode: _this4.selectRows.areaCode,
            locationCode: _this4.selectRows.locationCode,
            warehouseCode: _this4.selectRows.warehouseCode,
            orgCode: _this4.ckbmRows.orgCode,
            orgName: _this4.ckbmRows.orgName,
            materielCode: item.materielCode,
            materielName: item.materielName,
            locationMaterielRule: 'materiel'
          };
          dtos.push(obj);
        });
        api.addRecord1(dtos).then(function () {
          _this4.getlistSub();

          _this4.wlxzDialogFormVisible = false;

          _this4.$notify({
            title: "成功",
            message: "新增成功",
            type: "success",
            duration: 2000
          });
        });
      } else {
        this.$message({
          title: "警告",
          message: "请选择一条信息",
          type: "warning"
        });
      }
    },
    update3: function update3() {
      //新增物料编码弹窗确认按钮
      this.formWlRows = this.$refs.wlxzRef.listSelected;

      if (this.formWlRows && this.formWlRows.length == 1) {
        this.addList.materielCode = this.formWlRows[0].materielCode;
        this.addList.materielName = this.formWlRows[0].materielName;
        this.wlxzDialogFormVisible = false;
      } else {
        this.$message({
          title: "警告",
          message: "请选择一条信息",
          type: "warning"
        });
      }
    },
    confirmKeeper: function confirmKeeper() {
      //参数设置保管员中弹窗确认按钮
      if (this.keeperRows && this.keeperRows.length == 1) {
        this.paramList.stockEmpName = this.keeperRows[0].employeeName;
        this.paramList.stockEmp = this.keeperRows[0].employeeCode;
        this.keeperFormVisibleParams = false;
      } else {
        this.$message({
          title: "警告",
          message: "请选择一条信息",
          type: "warning"
        });
      }
    },
    confirmKeeperForm: function confirmKeeperForm() {
      //新增编辑中弹窗确认按钮
      if (this.keeperRows && this.keeperRows.length == 1) {
        this.addList.stockEmpName = this.keeperRows[0].employeeName;
        this.addList.stockEmp = this.keeperRows[0].employeeCode;
        this.keeperFormVisibleParams = false;
      } else {
        this.$message({
          title: "警告",
          message: "请选择一条信息",
          type: "warning"
        });
      }
    },
