      //参数设置弹窗
      console.log(this.selectRowsSub, '000');

      if (this.selectRowsSub) {
        this.dialogFormVisibleParams = true;
        console.log(this.selectRowsSub, '000');
      } else {
        this.$message({
          title: "警告",
          message: "请选择信息",
          type: "warning"
        });
      }
    },
    createcssz: function createcssz() {
      var _this5 = this;

      //参数设置确认
      var rowIdsArry = [];
      rowIdsArry.push(this.selectRowsSub[0].rowId);
      this.paramList.rowIds = rowIdsArry;
      api.updateRecordcssz(this.paramList).then(function () {
        _this5.getlistSub();

        _this5.dialogFormVisibleParams = false;

        _this5.$notify({
          title: "成功",
          message: "设置成功",
          type: "success",
          duration: 2000
        });
      });
    },
