      var _this6 = this;

      //删除方法
      if (this.selectRowsSub) {
        this.$confirm(this.$t('message.isDeleteContinue'), this.$t('message.prompt'), {
          confirmButtonText: this.$t('message.yes'),
          cancelButtonText: this.$t('message.no'),
          type: 'warning'
        }).then(function () {
          api.deleteRecord(_this6.selectRowsSub[0].rowId).then(function () {
            _this6.getlistSub();

            _this6.$notify({
              title: "成功",
              message: "删除成功",
              type: "success",
              duration: 2000
