          });
        });
      } else {
        this.$message({
          title: "警告",
          message: "请选择一条信息",
          type: "warning"
        });
      }
    },
    handleCreatewl: function handleCreatewl() {
      var _this7 = this;

      //新增物料弹窗
      if (this.selectRows.warehouseCode) {
        this.dialogStatus = "confirmMateriel";
        this.wlxzDialogFormVisible = true;
        this.$nextTick(function () {
          _this7.unMutiple = false;
          _this7.$refs.wlxzRef.isEdit = true;

          _this7.$refs.wlxzRef.getList();
        });
      } else {
        this.$message({
          title: "警告",
          message: "请选择一条信息",
          type: "warning"
        });
      }
    },
    handleCreate: function handleCreate() {
      //新增方法
      // this.resetTemp();
      if (this.selectRows.warehouseCode) {
        this.dialogStatus = "create";
        this.setDefault();
        this.dialogFormVisible = true;
      } else {
        this.$message({
          title: "警告",
          message: "请选择一条信息",
          type: "warning"
        });
      }
    },
    handleUpdate: function handleUpdate() {
      //编辑方法
      // this.resetTemp();
      if (this.selectRowsSub) {
        this.dialogStatus = "update";
        this.setDefault();
        this.addList.state = this.selectRowsSub[0].state;
        this.addList.materielCode = this.selectRowsSub[0].materielCode;
        this.addList.materielName = this.selectRowsSub[0].materielName;
        this.addList.stockEmp = this.selectRowsSub[0].stockEmp;
        this.addList.materialputEmpName = this.selectRowsSub[0].materialputEmpName;
        this.dialogFormVisible = true;
      } else {
        this.$message({
          title: "警告",
          message: "请选择一条信息",
          type: "warning"
        });
      }
    },
    handleLead: function handleLead() {
      var _this8 = this;

      this.importDialogVisible = true;
      this.$nextTick(function () {
        _this8.$refs.importDialog.getList();
      });
    },
    handlegPersonnel: function handlegPersonnel() {
      //业务人员
      if (this.selectRowsSub) {
        this.dialogStatusParam = "keeperParam";
        this.keeperFormVisibleParams = true;
      } else {
        this.$message({
          title: "警告",
          message: "请选择一条物料信息",
          type: "warning"
        });
      }
    },
    handlegPersonnelForm: function handlegPersonnelForm() {
      if (this.selectRowsSub) {
        this.dialogStatusParam = "keeperForm";
        this.keeperFormVisibleParams = true;
      } else {
        this.$message({
          title: "警告",
          message: "请选择一条物料信息",
          type: "warning"
        });
      }
    },
    purchasingPersonnels: function purchasingPersonnels(row) {
      this.keeperRows = row;
    },
    handleClickMateriel: function handleClickMateriel() {
      var _this9 = this;

      this.wlxzDialogFormVisible = true;
      this.$nextTick(function () {
        _this9.unMutiple = true;
        _this9.$refs.wlxzRef.isEdit = true;

        _this9.$refs.wlxzRef.getList();
      });
    },
    //固定值赋值
    setDefault: function setDefault() {
      this.addList.orgName = this.ckbmRows.orgCode;
      this.addList.orgCode = this.ckbmRows.orgName;
      this.addList.warehouseCode = this.ckbmRows.warehouseCode;
      this.addList.warehouseName = this.ckbmRows.warehouseName;
      this.addList.areaName = this.selectRows.areaName;
      this.addList.locationName = this.selectRows.locationName;
      this.addList.locationCode = this.selectRows.locationCode;
      this.addList.areaCode = this.selectRows.areaCode;
      this.addList.stockEmp = this.selectRows.stockEmp;
      this.addList.stockEmpName = this.selectRows.stockEmpName;
    },
    create: function create() {
      var _this10 = this;

      // this.setDefault();
      this.addList.locationMaterielRule = "materiel";
      api.addRecord(this.addList).then(function () {
        _this10.getlistSub();

        _this10.dialogFormVisible = false;

        _this10.$notify({
          title: "成功",
          message: "新增成功",
          type: "success",
          duration: 2000
        });
      });
    },
    update: function update() {
      var _this11 = this;

      //编辑方法
      this.addList.locationMaterielRule = "materiel";
      api.updateRecord(this.selectRowsSub[0].rowId, this.addList).then(function () {
        _this11.getlistSub();

        _this11.dialogFormVisible = false;

        _this11.$notify({
          title: "成功",
          message: "编辑成功",
          type: "success",
          duration: 2000
        });
      });
    },
    //仓库编码确认
    confirmWarehouseCode: function confirmWarehouseCode() {
      if (this.ckbmRows) {
        this.listQuery.warehouseCode = this.ckbmRows.warehouseCode;
        this.ckbmDialogFormVisible = false;
      } else {
        this.$message({
          title: "警告",
          message: "请选择一条信息",
          type: "warning"
        });
      }
    },
    handleQuery: function handleQuery() {
      this.listQuery.currentPage = 1;
      this.getList();
    },
    handleClose: function handleClose() {
      this.resetTemp();
      this.$refs.maintb.resetFields();
    },
    handleCloseParams: function handleCloseParams() {
      this.keeperFormVisibleParams = false;
    },
    resetTemp: function resetTemp() {
      this.addList = {
        warehouseCode: undefined,
        warehouseName: undefined,
        materielCode: undefined,
        materielName: undefined,
        locationCode: undefined,
        locationName: undefined,
        areaCode: undefined,
        areaName: undefined,
        stockEmp: undefined,
        stockEmpName: undefined,
        goodsReadyEmp: undefined,
        goodsReadyEmpName: undefined,
        materialputEmp: undefined,
        materialputEmpName: undefined,
        state: 1,
        orgName: undefined,
        orgCode: undefined
      };
    }
  }
};
