import { queryDeviceBinds, updateDeliver, queryCurve, commandIssue } from "@/api/project/iot/IOT/db";
import throttle from 'lodash/throttle';
export default {
  name: "DBTimeData",
  props: {
    itemData: {
      type: Object,
      default: undefined
    },
    code: {
      type: String,
      default: undefined
    }
  },
  watch: {
    itemData: function itemData(val) {
      if (val) {
        this.getList();
      }
    }
  },
  data: function data() {
    return {
      listLoading: false,
      theight: 0,
      //表格高度
      list: [],
      tableKey: 0,
      //表格索引
      pointCode: undefined,
      listQuery: {
        pointEndTime: undefined,
        pointStartTime: undefined
      },
      temp: {},
      row: undefined,
      dialogFormVisible: false,
      dialogFormVisibleSub: false,
      chartList: null,
      rules: {
        value: [{
          required: true,
          message: '下发值不能为空',
          trigger: 'blur'
        }]
      }
    };
  },
  mounted: function mounted() {
    var _this = this;

    this.setTableHeight(); //表格高度自适应

    window.onresize = function () {
      _this.setTableHeight();
    };
  },
  methods: {
    stateClass: function stateClass(state) {
      if (state == '0') {
        return 'iot-warning';
      } else if (state == '1') {
        return 'iot-success';
      } else if (state == '2') {
        return 'iot-info';
      }
    },
    //获取表格数据
    getList: function getList() {
      var _this2 = this;

      this.listLoading = true;
      queryDeviceBinds(this.code, this.itemData.gatewayCode, this.itemData.deviceCode).then(function (response) {
        _this2.$emit('getSubList', response.data);

        _this2.list = response.data; // let options = [this.iot_state]
        // this.list = this.$myFun.srmCodeToName(
        //     response.data,
        //     options,
        //     ['deliveryState']
        // )

        setTimeout(function () {
          _this2.listLoading = false;
        }, 1.5 * 100);
      });
    },
    // 交付
    pay: function pay(row, pointCode) {
      var _this3 = this;

      this.$confirm('此操作将交付所选中数据, 是否继续?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function () {
        updateDeliver(_this3.code, _this3.itemData.gatewayCode, _this3.itemData.deviceCode, pointCode).then(function () {
          _this3.getList();

          _this3.$message({
            title: '成功',
            message: '交付成功',
            type: 'success',
            duration: 2000
          });
        });
      }).catch(function () {
        _this3.$message({
          type: 'info',
          message: '已取消交付'
        });
      });
    },
    //命令下发
    handleorderIssue: function handleorderIssue(row) {
      console.log(row, 111);
      this.temp = row;
      this.temp.deviceName = this.itemData.deviceName;
      this.dialogFormVisibleSub = true;
    },
    //命令下发
    orderIssue: throttle(function () {
      var _this4 = this;

      this.$refs.tempRef.validate(function (valid) {
        if (valid) {
          commandIssue(_this4.code, _this4.itemData.gatewayCode, _this4.itemData.deviceCode, _this4.temp.pointCode, _this4.temp.value).then(function (res) {
            _this4.getList();

            _this4.dialogFormVisibleSub = false;

            if (res.data == '1') {
              return _this4.$notify({
                title: '成功',
                message: '下发成功',
                type: 'success',
                duration: 2000
              });
            } else {
              return _this4.$notify({
                title: '失败',
                message: '下发失败',
                type: 'error',
                duration: 2000
              });
            }
          });
        } else {
          return false;
        }
      });
    }, 2000, {
      leading: true,
      trailing: false
    }),
    // 查看曲线
    handleViewCurves: function handleViewCurves(pointCode) {
      //获取当前时间
      var day = new Date();
      var now = new Date();
      day.setTime(day.getTime() - 2 * 60 * 60 * 1000);
      this.listQuery.pointStartTime = getDate(day);
      this.listQuery.pointEndTime = getDate(now);

      function getDate(day) {
        var year = day.getFullYear(); //得到年份

        var month = day.getMonth() + 1 > 10 ? day.getMonth() + 1 : '0' + (day.getMonth() + 1); //得到月份

        var date = day.getDate() > 10 ? day.getDate() : '0' + day.getDate(); //得到日期

        var hour = day.getHours() > 10 ? day.getHours() : '0' + day.getHours(); //得到小时

        var minu = day.getMinutes() > 10 ? day.getMinutes() : '0' + day.getMinutes(); //得到分钟

        var sec = day.getSeconds() > 10 ? day.getSeconds() : '0' + day.getSeconds(); //得到秒

        return "".concat(year, "-").concat(month, "-").concat(date, " ").concat(hour, ":").concat(minu, ":").concat(sec);
      }

      this.pointCode = pointCode;
      this.dialogFormVisible = true;
      this.handleQuery();
    },
    // 搜索曲线
    handleQuery: function handleQuery() {
      var _this5 = this;

      queryCurve(this.code, this.itemData.gatewayCode, this.itemData.deviceCode, this.pointCode, this.listQuery).then(function (res) {
        _this5.chartList = res.data;

        _this5.drawLine();
      });
    },
    drawLine: function drawLine() {
      var xAxisData = this.chartList.map(function (item) {
        return item.pointTime;
      });
      var data = this.chartList.map(function (item) {
        return {
          value: item.pointValue,
          name: item.pointName
        };
      }); // 基于准备好的dom，初始化echarts实例

      var myChart = this.$echarts.init(this.$refs.myChart); // 绘制图表

      myChart.setOption({
        title: {
          text: ''
        },
        tooltip: {
          formatter: '{b}<br/>{c}'
        },
        xAxis: {
          data: xAxisData
        },
        yAxis: {},
        series: [{
          name: '',
          type: 'line',
          data: data
        }]
      });
    },
    //表格高度计算
    setTableHeight: function setTableHeight() {
      this.theight = this.$myFun.getSingleTbHeight() - 30;
    }
  }
};
