import "core-js/modules/es6.array.sort";
import "core-js/modules/web.dom.iterable";
//
//
//
//
//
//
//
export default {
  name: "capsuleBar",
  props: ['id', 'data'],
  data: function data() {
    return {
      mycharts: undefined
    };
  },
  watch: {
    data: function data() {
      this.getPie();
    }
  },
  mounted: function mounted() {
    var _this = this;

    window.addEventListener('resize', function () {
      if (_this.mycharts) {
        _this.mycharts.resize();
      }
    });
    this.getPie();
  },
  methods: {
    getContent: function getContent(max) {
      var content = 0;

      if (max <= 10) {
        content = 10;
      } else if (max <= 50) {
        content = Math.ceil(max / 10) * 10;
      } else if (max < 100) {
        content = 100;
      } else if (max <= 1000) {
        content = Math.ceil(max / 1000) * 1000;
      } else {
        content = max;
      }

      return content;
    },
    getPie: function getPie() {
      if (!this.data) return false;
      this.myChart = this.$echarts.init(document.getElementById(this.id));
      var subData = [];
      var max = Math.max.apply(null, this.data.data);
      var content = this.getContent(max);
      this.data.data.forEach(function (item) {
        subData.push({
          name: item,
          value: content
        });
      });
      var option = {
        grid: {
          // 直角坐标系内绘图网格
          left: '25%',
          //grid 组件离容器左侧的距离,
          //left的值可以是80这样具体像素值，
          //也可以是'80%'这样相对于容器高度的百分比
          top: '10',
          right: '15%',
          bottom: '10',
          containLabel: false //gid区域是否包含坐标轴的刻度标签。为true的时候，
          // left/right/top/bottom/width/height决定的是包括了坐标轴标签在内的
          //所有内容所形成的矩形的位置.常用于【防止标签溢出】的场景

        },
        tooltip: {
          trigger: 'axis',
          axisPointer: {
            // 坐标轴指示器，坐标轴触发有效
            type: 'shadow' // 默认为直线，可选为：'line' | 'shadow'

          },
          formatter: function formatter(params) {
            var showHtm = "";

            for (var i = 0; i < params.length; i++) {
              showHtm = params[0].axisValue + ':' + params[0].data;
            }

            return showHtm;
          }
        },
        xAxis: {
          //直角坐标系grid中的x轴,
          //一般情况下单个grid组件最多只能放上下两个x轴,
          //多于两个x轴需要通过配置offset属性防止同个位置多个x轴的重叠。
          type: 'value',
          //坐标轴类型,分别有：
          //'value'-数值轴；'category'-类目轴;
          //'time'-时间轴;'log'-对数轴
          splitLine: {
            show: false
          },
          //坐标轴在 grid 区域中的分隔线
          axisLabel: {
            show: false
          },
          //坐标轴刻度标签
          axisTick: {
            show: false
          },
          //坐标轴刻度
          axisLine: {
            show: false
          } //坐标轴轴线

        },
        yAxis: {
          type: 'category',
          axisTick: {
            show: false
          },
          axisLine: {
            show: false
          },
          axisLabel: {
            color: '#FFF',
            fontSize: 12
          },
          data: this.data.yAxisData //类目数据，在类目轴（type: 'category'）中有效。
          //如果没有设置 type，但是设置了axis.data,则认为type 是 'category'。

        },
        series: [//系列列表。每个系列通过 type 决定自己的图表类型
        {
          name: '%',
          //系列名称
          type: 'bar',
          //柱状、条形图
          barWidth: 10,
          //柱条的宽度,默认自适应
          data: this.data.data.sort(function (a, b) {
            return a - b;
          }),
          //系列中数据内容数组
          itemStyle: {
            //图形样式
            normal: {
              //normal 图形在默认状态下的样式;
              //emphasis图形在高亮状态下的样式
              barBorderRadius: [0, 10, 10, 0],
              //柱条圆角半径,单位px.
              //此处统一设置4个角的圆角大小;
              //也可以分开设置[10,10,10,10]顺时针左上、右上、右下、左下
              color: new this.$echarts.graphic.LinearGradient(0, 0, 1, 0, [{
                offset: 0,
                color: '#0569e1' //柱图渐变色起点颜色

              }, {
                offset: 1,
                color: '#05b4f7' //柱图渐变色终点颜色

              }])
            }
          },
          zlevel: 9 //柱状图所有图形的 zlevel 值,
          //zlevel 大的 Canvas 会放在 zlevel 小的 Canvas 的上面

        }, {
          name: '进度条背景',
          type: 'bar',
          barGap: '-100%',
          //不同系列的柱间距离，为百分比。
          // 在同一坐标系上，此属性会被多个 'bar' 系列共享。
          // 此属性应设置于此坐标系中最后一个 'bar' 系列上才会生效，
          //并且是对此坐标系中所有 'bar' 系列生效。
          barWidth: 10,
          data: subData,
          color: 'rgba(0,0,0,0.5)',
          //柱条颜色
          label: {
            //图形上的文本标签
            show: true,
            position: 'right',
            //标签的位置
            offset: [0, 0],
            //标签文字的偏移，此处表示向上偏移40
            formatter: '{b}%',
            //标签内容格式器 {a}-系列名,{b}-数据名,{c}-数据值
            color: '#FFF',
            //标签字体颜色
            fontSize: 12 //标签字号

          },
          itemStyle: {
            normal: {
              barBorderRadius: [0, 10, 10, 0],
              //柱条圆角半径,单位px.
              color: 'rgba(255,255,255,0.5)' //柱条颜色

            }
          }
        }]
      };
      this.myChart.setOption(option, true);
    }
  }
};
