    },
    //选中标签列表
    selectPackRow: function selectPackRow(val) {
      this.selectPackRows = val;
    },
    //批量打印
    handleBulkPrint: function handleBulkPrint() {
      var _this14 = this;

      if (this.selectPackRows.length === 0) {
        this.$message({
          title: this.$t('message.warning'),
          message: '请选择要打印的标签',
          type: "warning"
        });
      } else {
        var printable = true;

        try {
          this.selectPackRows.forEach(function (item) {
            if (item.printSign === 1) {
              printable = false;

              _this14.$message({
                title: _this14.$t('message.warning'),
                message: '所选项包含已打印过的标签，不能进行批量打印',
                type: "warning"
              });

              throw '';
            }
          });

          if (printable) {
            this.updateMethod = 'batchPrint';
            this.createPrintPack();
          }
        } catch (e) {
          console.log(e);
        }
      }
    },
    //补打
    handleFillPrint: function handleFillPrint() {
      var _this15 = this;

      if (this.selectPackRows.length === 0) {
        this.$message({
          title: this.$t('message.warning'),
          message: '请选择要打印的标签',
          type: "warning"
        });
      } else {
        var printable = true;

        try {
          this.selectPackRows.forEach(function (item) {
            if (item.printSign === 0) {
              printable = false;

              _this15.$message({
                title: _this15.$t('message.warning'),
                message: '所选项包含未打印过的标签，不能进行补打',
                type: "warning"
              });
            }
          });

          if (printable) {
            this.updateMethod = 'makeUpPrint';
            this.createPrintPack();
          }
        } catch (e) {
          console.log(e);
        }
      }
    },
    initUpdatePacklist: function initUpdatePacklist() {
      var barcodes = new Array();
      this.selectPackRows.forEach(function (item) {
        barcodes.push(item.barCode);
      });
      this.updatePacklist.barCode = barcodes;
      this.updatePacklist.method = this.updateMethod;

      if (this.updateMethod !== 'setUp') {
        this.updatePacklist.supplierBatch = '';
        this.updatePacklist.supplierRemark = '';
      }
    },
    //生成打印内容
    createPrintPack: function createPrintPack() {
      var _this16 = this;

      this.dialogPackPrintVisible = true;
      this.packPrintList = this.selectPackRows;
      this.$nextTick(function () {
        _this16.packPrintList.forEach(function (item) {
          _this16.$refs['childQRcode' + item.rowId][0].qrCreate(item.qrCode);

          JsBarcode("#barcode" + item.rowId, item.barCode);
        });
      });
    },
    //标签设置确认提交
    confirmPackBatch: function confirmPackBatch() {
      var _this17 = this;

      this.updateMethod = 'setUp';
      this.initUpdatePacklist();
      api.updatePackTag(this.updatePacklist).then(function () {
        api.queryPackTag(_this17.listQueryPack).then(function (resq) {
          _this17.packDataList = resq;
        });

        _this17.$message.success('设置成功');
      });
    },
    //执行包装标签打印
    doPackPrint: function doPackPrint() {
