      this.$confirm('是否进行打印操作？', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function () {
        _this18.dialogPackPrintVisible = false;

        _this18.initUpdatePacklist();

        api.updatePackTag(_this18.updatePacklist).then(function () {
          api.queryPackTag(_this18.listQueryPack).then(function (resq) {
            _this18.packDataList = resq;
          });

          _this18.$message.success('设置成功');
        });
        var newWin = window.open("");
        var newstr = document.getElementById('packprint').innerHTML;
        newWin.document.write(newstr);
        newWin.document.close(); //IE添加

        setTimeout(function () {
          newWin.print();
          newWin.close();
        }, 100);
      });
    }
