import request from '@/utils/request'; // 创建库内加工凭证

export function createProof(data) {
  return request({
    url: '/iwms/stores/processings',
    method: 'post',
    data: data
  });
} //库内加工凭证信息查询

export function queryRecords(orgCode, warehouseCode, dicType, query) {
  return request({
    url: "/iwms/stores/processings/".concat(orgCode, "/").concat(warehouseCode, "/").concat(dicType),
    method: 'get',
    params: query
  });
} //库内加工凭证明细查询

export function queryRecordsDetail(proofNo, query) {
  return request({
    url: "/iwms/stores/processings/".concat(proofNo, "/detail"),
    method: 'get',
    params: query
  });
} //获取区位

export function getArea(warehouseCode) {
  return request({
    url: "/base-modeling/warehouses/".concat(warehouseCode, "/areas"),
    method: 'get'
  });
} //获取储位

export function getLocator(warehouseCode, areaCode) {
  return request({
    url: "/base-modeling/warehouses/".concat(warehouseCode, "/areas/").concat(areaCode, "/locations"),
    method: 'get'
  });
}
export function queryStock(code) {
  return request({
    url: '/iwms/warehouses/rules',
    method: 'get',
    params: {
      currentPage: 0,
      pageSize: 15,
      wareRuleCode: code,
      wareRuleName: '',
      statsClassify: ''
    }
  });
}
