import request from '@/utils/request'; //采购组织物料参数查询

export function queryRecords(query) {
  return request({
    url: "/basic/material-parameters",
    method: 'get',
    params: query
  });
} //采购组织物料参数（新增）

export function addRecord(data) {
  return request({
    url: "/basic/material-parameters",
    method: 'post',
    data: data
  });
} //采购组织物料参数（编辑）

export function updateRecord(rowId, data) {
  return request({
    url: "/basic/material-parameters/".concat(rowId),
    method: 'patch',
    data: data
  });
} //采购组织物料参数删除

export function deleteRecord(rowId) {
  return request({
    url: "/basic/material-parameters/".concat(rowId),
    method: 'delete'
  });
}
