import * as api from "@/api/data-base/exemption-list";
import Pagination from "@/components/Pagination";
import orgSelect from "@/components/Select/orgSelect.vue";
import { DICT_CODE, DT_ORG_FUNCTION, DT_ORG_TYPE, THE_STATE_OPTIONS } from "@/utils/constant";
import gysxz from "@/components/Select/gysxz"; //供应商选择

import wlxz from "@/components/Select/wlxzbySourceList"; //物料信息模块

export default {
  name: "exemptionList",
  components: {
    Pagination: Pagination,
    orgSelect: orgSelect,
    gysxz: gysxz,
    wlxz: wlxz
  },
  data: function data() {
    return {
      list: null,
      list1: null,
      total: 0,
      total1: 0,
      theight: 0,
      wlsupplierCode: "",
      wlorgCode: "",
      ok: false,
      listQuery: {
        page: true,
        currentPage: 1,
        pageSize: 10,
        orgPurchas: undefined,
        arrivalCompany: undefined,
        supplierName: undefined
      },
      listQuery1: {
        page: true,
        currentPage: 1,
        pageSize: 10,
        supplierCode: undefined,
        orderNumber: undefined,
        uncheckId: undefined
      },
      temp: {
        orgPurchas: undefined,
        arrivalCompany: undefined,
        supplierCode: undefined,
        rowId: undefined,
        supplierName: undefined,
        classify: undefined,
        materielCode: undefined,
        materielName: undefined,
        uncheckType: 1
      },
      uncheckDTO: {
        orgCode: undefined,
        orgName: undefined,
        rowId: undefined,
        supplierCode: undefined,
        supplierName: undefined,
        toOrgCode: undefined,
        toOrgName: undefined,
        uncheckType: undefined,
        method: undefined
      },
      uncheckItemsDTOList: [],
      dto: {
        orgCode: undefined,
        orgName: undefined,
        rowId: undefined,
        supplierCode: undefined,
        supplierName: undefined,
        toOrgCode: undefined,
        toOrgName: undefined,
        uncheckType: undefined,
        method: undefined
      },
      del: {
        orgCode: undefined,
        orgName: undefined,
        rowId: undefined,
        supplierCode: undefined,
        supplierName: undefined,
        toOrgCode: undefined,
        toOrgName: undefined,
        uncheckType: undefined
      },
      dtodel: {
        materielCode: undefined,
        materielName: undefined,
        rowId: undefined
      },
      gysxzDialogFormVisible: undefined,
      gysxzDialogFormVisible1: undefined,
      // measureCode: undefined,
      selectCode: [],
      dialogFormVisible: false,
      parentsRows: [],
      dialogStatus: "",
      tableKey: [0, 1],
      //表格索引，虚拟dom渲染表格时使用
      isChangeAddS: false,
      DICT_CODE: DICT_CODE,
      DT_ORG_FUNCTION: DT_ORG_FUNCTION,
      DT_ORG_TYPE: DT_ORG_TYPE,
      // dimensionOptions: DICT_CODE,
      THE_STATE_OPTIONS: THE_STATE_OPTIONS
    };
  },
  created: function created() {
    this.getList();
  },
  mounted: function mounted() {
    var _this = this;

    this.setTableHeight(); //表格高度自适应

    window.onresize = function () {
      _this.setTableHeight();
    };
  },
  methods: {
    getList: function getList() {
      var _this2 = this;

      //查询主表
      api.queryRecords(this.listQuery).then(function (res) {
        _this2.list = res.data.list;
        _this2.total = res.data.pages.count;
      });
    },
    //表格高度计算
    setTableHeight: function setTableHeight() {
      this.theight = this.$myFun.getSingleTbHeight() - 185;
    },
    getList1: function getList1() {
      var _this3 = this;

      //查询订单明细
      this.listQuery1.uncheckId = this.selectlistRow.rowId;
      api.queryRecordsmx(this.listQuery1).then(function (res) {
        _this3.list1 = res.data.list;
        _this3.total1 = res.data.pages.count;
      });
    },
    //供应商显示
    showGys: function showGys(val) {
      this.gysValue = val;
      this.$refs.dialogRefgys.dialogVisible = true;
    },
    //供应商确认
    handleGysClick: function handleGysClick(gysRows) {
      this.supplierRows = gysRows;

      if (this.gysValue === 'query') {
        if (!this.supplierRows[0]) {
          this.listQuery.supplierCode = "";
          this.listQuery.supplierName = "";
          this.$refs.dialogRefgys.dialogVisible = false;
        } else if (this.supplierRows.length === 1) {
          this.listQuery.supplierCode = this.supplierRows[0].supplierCode;
          this.listQuery.supplierName = this.supplierRows[0].supplierName;
          this.$refs.dialogRefgys.dialogVisible = false;
        }
      } else if (this.gysValue === 'add') {
        if (!this.supplierRows[0]) {
          this.$message({
            title: this.$t("message.warning"),
            message: this.$t("message.checkedplease"),
            type: "warning"
          });
        } else if (this.supplierRows.length === 1) {
          this.temp.supplierCode = this.supplierRows[0].supplierCode;
          this.temp.supplierName = this.supplierRows[0].supplierName;
          this.$refs.dialogRefgys.dialogVisible = false;
        }
      }
    },
    handleCreate: function handleCreate() {
      //主表新增
      this.resetTemp();
      this.dialogStatus = "create";
      this.dialogFormVisible = true;
    },
    orgname: function orgname(name) {
      this.uncheckDTO.orgName = name;
    },
    orgname1: function orgname1(name) {
      this.uncheckDTO.toOrgName = name;
    },
    create: function create() {
      var _this4 = this;

      //主表新增
      this.uncheckDTO.orgCode = this.temp.orgCode; // this.uncheckDTO.orgName=this.temp.orgName

      this.uncheckDTO.supplierCode = this.temp.supplierCode;
      this.uncheckDTO.supplierName = this.temp.supplierName;
      this.uncheckDTO.toOrgCode = this.temp.toOrgCode; // this.uncheckDTO.toOrgName=this.temp.toOrgName

      this.uncheckDTO.uncheckType = this.temp.uncheckType;
      var dd = {
        uncheckDTO: this.uncheckDTO,
        uncheckItemsDTOList: [],
        //不能为空
        method: "uncheck"
      };
      console.log(dd);
      this.method = "uncheck";
      api.addRecord1(dd).then(function () {
        _this4.dialogFormVisible = false;

        _this4.$notify({
          title: "成功",
          message: "新增成功",
          type: "success",
          duration: 2000
        });

        console.log(1);

        _this4.getList();
      });
    },
    handleCreatemx: function handleCreatemx() {
      var _this5 = this;

      //新增明细
      if (this.selectlistRow) {
        setTimeout(function () {
          _this5.$refs.wlxzRef.listQuery.orgCode = _this5.selectlistRow.orgCode;
          _this5.$refs.wlxzRef.listQuery.supplierCode = _this5.selectlistRow.supplierCode;

          _this5.$refs.wlxzRef.getList();
        }, 0);
        this.dialogStatus = "create1";
        this.gysxzDialogFormVisible1 = true;
      } else {
        this.$message({
          title: "警告",
          message: "请选择一条信息",
          type: "warning"
        });
      }
    },
    create1: function create1() {
      var _this6 = this;

      //新增明细
      this.parentsRows2 = this.$refs.wlxzRef.listSelected;

      if (this.parentsRows2 && this.parentsRows2.length > 0) {
        console.log(this.parentsRows2);
        this.uncheckItemsDTOList = [];

        for (var i = 0; i < this.parentsRows2.length; i++) {
          this.uncheckItemsDTOList[i] = {}; //必须先声明它里面的每一项都是集合

          this.uncheckItemsDTOList[i].rowId = this.selectlistRow.rowId;
          this.uncheckItemsDTOList[i].materielCode = this.parentsRows2[i].materielCode;
          this.uncheckItemsDTOList[i].materielName = this.parentsRows2[i].materielName;
        }

        console.log(this.uncheckItemsDTOList);
        var mx = {
          uncheckDTO: {},
          uncheckItemsDTOList: this.uncheckItemsDTOList,
          method: "uncheckItems"
        };
        api.addRecord(mx).then(function () {
          _this6.getList1();

          _this6.gysxzDialogFormVisible1 = false;

          _this6.$notify({
            title: "成功",
            message: "新增成功",
            type: "success",
            duration: 2000
          });

          _this6.$refs.wlxzRef.clearSelection();
        });
      } else {
        this.$message({
          title: "警告",
          message: "请选择信息",
          type: "warning"
        });
      }
    },
    handleUpdate: function handleUpdate() {
      //编辑
      this.resetTemp();

      if (this.selectlistRow) {
        this.dialogStatus = "update";
        this.dialogFormVisible = true;
        this.temp.orgCode = this.selectlistRow.orgCode;
        this.temp.toOrgCode = this.selectlistRow.toOrgCode;
        this.temp.supplierCode = this.selectlistRow.supplierCode;
        this.temp.supplierName = this.selectlistRow.supplierName;
        this.temp.uncheckType = this.selectlistRow.uncheckType;
      } else {
        this.$message({
          title: "警告",
          message: "请选择一条信息",
          type: "warning"
        });
      }
    },
    update: function update() {
      var _this7 = this;

      //编辑
      this.dto.orgCode = this.temp.orgCode;
      this.dto.orgName = this.selectlistRow.orgName; // console.log(this.selectlistRow1.orgName)

      this.dto.supplierCode = this.temp.supplierCode;
      this.dto.supplierName = this.temp.supplierName;
      this.dto.toOrgCode = this.temp.toOrgCode;
      this.dto.toOrgName = this.selectlistRow.toOrgName;
      this.dto.uncheckType = this.temp.uncheckType;
      console.log(this.dto.orgName);
      api.updateRecord(this.selectlistRow.rowId, this.dto).then(function () {
        _this7.getList();

        _this7.dialogFormVisible = false;

        _this7.$notify({
          title: "成功",
          message: "新增成功",
          type: "success",
          duration: 2000
        });
      });
    },
    handleDelete: function handleDelete() {
      var _this8 = this;

      //主表删除
      if (this.selectlistRow) {
        this.$confirm(this.$t("message.isDeleteContinue"), this.$t("message.prompt"), {
          confirmButtonText: this.$t("message.confirm"),
          cancelButtonText: this.$t("message.cancel"),
          type: "warning"
        }).then(function () {
          _this8.del = _this8.selectlistRow;
          api.deleteRecord(_this8.del).then(function () {
            _this8.getList();

            _this8.dialogFormVisible = false;

            _this8.$notify({
              title: _this8.$t("message.success"),
              message: _this8.$t("message.deleteSuccessed"),
              type: "success",
              duration: 2000
            });
          });
        }).catch(function () {
          _this8.$message({
            type: "info",
            message: _this8.$t("message.deleteCanceled")
          });
        });
      } else {
        this.$message({
          title: this.$t("message.warning"),
          message: this.$t("message.checkedoneplease"),
          type: "warning"
        });
      }
    },
    handleDelete1: function handleDelete1() {
      var _this9 = this;

      //删除明细
      if (this.selectlistRow2 && this.selectlistRow2.length == 1) {
        this.$confirm(this.$t("message.isDeleteContinue"), this.$t("message.prompt"), {
          confirmButtonText: this.$t("message.confirm"),
          cancelButtonText: this.$t("message.cancel"),
          type: "warning"
        }).then(function () {
          console.log(_this9.selectlistRow2[0].rowId);
          _this9.dtodel.rowId = _this9.selectlistRow2[0].rowId;
          _this9.dtodel.materielCode = _this9.selectlistRow2[0].materielCode;
          _this9.dtodel.materielName = _this9.selectlistRow2[0].materielName;
          api.deleteRecordmx(_this9.dtodel).then(function () {
            _this9.getList1();

            _this9.dialogFormVisible = false;

            _this9.$notify({
              title: _this9.$t("message.success"),
              message: _this9.$t("message.deleteSuccessed"),
              type: "success",
              duration: 2000
            });
          });
        }).catch(function () {
          _this9.$message({
            type: "info",
            message: _this9.$t("message.deleteCanceled")
          });
        });
      } else {
        this.$message({
          title: this.$t("message.warning"),
          message: this.$t("message.checkedoneplease"),
          type: "warning"
        });
      }
    },
    handleQuery: function handleQuery() {
      this.listQuery.currentPage = 1;
      this.getList();
    },
    handleSizeChange: function handleSizeChange(val) {
      this.listQuery.pageSize = val;
      this.getList();
    },
    handleCurrentChange: function handleCurrentChange(val) {
      this.listQuery.currentPage = val;
      this.getList();
    },
    handleClickgysxz: function handleClickgysxz() {
      this.$refs.dialogRefgys.dialogVisible = true;
    },
    handleDblclickgysxz: function handleDblclickgysxz(row) {
      //父子页面通信
      this.parentsRows = row; // this.gysxzDialogFormVisible = false;
    },
    showGysxz: function showGysxz() {
      this.isChangeAddS = true;
      this.gysxzDialogFormVisible = !this.gysxzDialogFormVisible;
    },
    update1: function update1() {
      //供应商选中弹窗确认按钮
      if (!this.isChangeAddS) {
        if (!this.parentsRows[0]) {
          this.listQuery.supplierCode = "";
          this.listQuery.supplierName = "";
          this.gysxzDialogFormVisible = false;
        } else if (this.parentsRows.length === 1) {
          this.listQuery.supplierCode = this.parentsRows[0].supplierCode;
          this.listQuery.supplierName = this.parentsRows[0].supplierName;
          this.gysxzDialogFormVisible = false;
        }
      } else {
        if (!this.parentsRows[0]) {
          this.$message({
            title: this.$t("message.warning"),
            message: this.$t("message.checkedplease"),
            type: "warning"
          });
        } else if (this.parentsRows.length === 1) {
          this.temp.supplierCode = this.parentsRows[0].supplierCode;
          this.temp.supplierName = this.parentsRows[0].supplierName;
          this.gysxzDialogFormVisible = false;
        }

        this.isChangeAddS = false;
      }
    },
    handleDeleteBatch: function handleDeleteBatch() {
      if (this.selectCode.length == 0) {
        this.$message.warning("请勾选记录！");
        return;
      }

      this.delete(this.selectCode);
    },
    selectRow: function selectRow(val) {
      //表一复选框选中方法
      this.selectlistRow = val[val.length - 1];

      if (val.length > 1) {
        this.$refs.tb.clearSelection();
        this.$refs.tb.toggleRowSelection(val.pop());
      }

      this.$refs.tb.setCurrentRow(this.selectlistRow);

      if (this.selectlistRow) {
        this.getList1();
      }
    },
    selectRow1: function selectRow1(val) {
      //表一单击行选中方法
      this.selectlistRow1 = val;
      this.$refs.tb.clearSelection();
      this.$refs.tb.toggleRowSelection(val); //单击行绑定点击事件
    },
    selectRow2: function selectRow2(val) {
      //明细表复选框选中方法
      this.selectlistRow2 = val;
    },
    cancel: function cancel() {
      this.dialogFormVisible = false;
    },
    resetTemp: function resetTemp() {
      this.temp = {
        measureCode: undefined,
        measureName: undefined,
        dimension: undefined,
        transferRate: undefined,
        isBase: 0,
        isEnabled: 1,
        remark: undefined,
        uncheckType: 1,
        supplierCode: undefined,
        supplierName: undefined
      };
    },
    handleClose: function handleClose() {
      this.resetTemp();
      this.$refs.temp.resetFields();
    },
    handleSelectionChange: function handleSelectionChange(selection) {
      this.selectCode = selection.map(function (v) {
        return v.measureCode;
      });
    }
  }
};
