//考察计划api
import request from '@/utils/request'; //查询考察计划列表

export function queryRecords(query) {
  return request({
    url: "/SRMsupplier/investigates",
    method: 'get',
    params: query
  });
} // 新增考察计划

export function createRecords(data) {
  return request({
    url: "/SRMsupplier/investigates",
    method: 'post',
    data: data
  });
} //查询单个考察计划

export function queryRow(investigateNo) {
  return request({
    url: "/SRMsupplier/investigates/".concat(investigateNo),
    method: 'get'
  });
} // 删除考察计划

export function deleteRecords(investigateNo) {
  return request({
    url: "/SRMsupplier/investigates/".concat(investigateNo),
    method: 'delete'
  });
} // 更新、发布、审批考察计划

export function updateRecords(data) {
  return request({
    url: "/SRMsupplier/investigates/".concat(data.investigateDTO.investigateNo),
    method: 'patch',
    data: data
  });
} //查询考察分类列表

export function queryClasses(investigateNo, query) {
  return request({
    url: "/SRMsupplier/investigates/".concat(investigateNo, "/classes"),
    method: 'get',
    params: query
  });
} // 创建考察分类

export function createClasses(investigateNo, data) {
  return request({
    url: "/SRMsupplier/investigates/".concat(investigateNo, "/classes"),
    method: 'post',
    data: data
  });
} // 删除考察分类

export function deleteClasses(investigateNo, rowId) {
  return request({
    url: "/SRMsupplier/investigates/".concat(investigateNo, "/classes/").concat(rowId),
    method: 'delete'
  });
} //查询考察项目列表

export function queryProject(investigateNo, query) {
  return request({
    url: "/SRMsupplier/investigates/".concat(investigateNo, "/items"),
    method: 'get',
    params: query
  });
} // 创建考察项目

export function createProject(investigateNo, data) {
  return request({
    url: "/SRMsupplier/investigates/".concat(investigateNo, "/items"),
    method: 'post',
    data: data
  });
} //查询单个考察项目

export function queryProjectItem(investigateNo, rowId, query) {
  return request({
    url: "/SRMsupplier/investigates/".concat(investigateNo, "/items/").concat(rowId),
    method: 'get',
    params: query
  });
} // 删除考察项目

export function deleteProject(investigateNo, rowId) {
  return request({
    url: "/SRMsupplier/investigates/".concat(investigateNo, "/items/").concat(rowId),
    method: 'delete'
  });
} // 更新、反馈考察项目

export function updateProjectRecords(investigateNo, rowId, data) {
  return request({
    url: "/SRMsupplier/investigates/".concat(investigateNo, "/items/").concat(rowId),
    method: 'patch',
    data: data
  });
} //查询生效反馈

export function queryFeedback(query) {
  return request({
    url: "/SRMsupplier/investigates/feedback-items",
    method: 'get',
    params: query
  });
} //发布

export function publish(investigateNo) {
  return request({
    url: "/SRMsupplier/investigates/".concat(investigateNo),
    method: 'patch',
    data: {
      method: 'RELEASE'
    }
  });
} //审批

export function approve(investigateNo) {
  return request({
    url: "/SRMsupplier/investigates/".concat(investigateNo),
    method: 'patch',
    data: {
      method: 'APPROVAL'
    }
  });
}
