import "core-js/modules/web.dom.iterable";
import request from '@/utils/request'; //设备请购主信息查询

export function queryRecords(query) {
  return request({
    url: "/easDevice/requisitions",
    method: 'get',
    params: query
  });
} //设备请购明细信息查询

export function queryRecordsItems(query) {
  return request({
    url: "/easDevice/requisitions/".concat(query.requisitionsOrderCode, "/details"),
    method: 'get',
    params: query
  });
} //设备请购明细单个信息查询

export function Items(requisitionsOrderCode) {
  return request({
    url: "/easDevice/requisitions/".concat(requisitionsOrderCode),
    method: 'get'
  });
} //设备请购（新增）

export function addRecord(data) {
  return request({
    url: "/easDevice/requisitions",
    method: 'post',
    data: data
  });
} //设备请购（编辑）

export function updateRecord(requisitionsOrderCode, data) {
  return request({
    url: "/easDevice/requisitions/".concat(requisitionsOrderCode),
    method: 'patch',
    data: data
  });
} //设备请购主信息删除

export function deleteRecord(requisitionsOrderCode) {
  return request({
    url: "/easDevice/requisitions/".concat(requisitionsOrderCode),
    method: 'delete'
  });
} //设备请购（状态更新）

export function updateEffect(requisitionsOrderCode, query) {
  return request({
    url: "/easDevice/requisitions/".concat(requisitionsOrderCode, "/state"),
    method: 'patch',
    params: query
  });
} //设备请购明细信息批量删除

export function deleteRecordItems(requisitionsOrderCode, data) {
  var arr = '';
  data.forEach(function (i, index) {
    arr += "&rowNos=".concat(i);

    if (index !== data.length) {
      arr += '&';
    }
  });
  return request({
    url: "/easDevice/requisitions/".concat(requisitionsOrderCode, "/details?").concat(arr),
    method: 'delete'
  });
} //设备请购（到货）

export function arrival(requisitionsOrderCode, rowNo, data) {
  return request({
    url: "/easDevice/requisitions/".concat(requisitionsOrderCode, "/details/").concat(rowNo),
    method: 'patch',
    data: data
  });
} //设备请购（入台账）

export function account(requisitionsOrderCode, rowNo, data) {
  return request({
    url: "/easDevice/requisitions/".concat(requisitionsOrderCode, "/details/").concat(rowNo),
    method: 'post',
    data: data
  });
}
