import request from '@/utils/request'; // 日常评价单查询

export function queryRecord(query) {
  return request({
    url: "/SRMsupplier/evaluationSheet",
    method: 'get',
    params: query
  });
} // 批量新增日常评价单

export function batchAddRecord(data) {
  return request({
    url: "/SRMsupplier/evaluationSheet/sheet/batch",
    method: 'post',
    data: data
  });
} // 查询单个日常评价

export function queryOneRecord(evaluationNo, supplierCode) {
  return request({
    url: "/SRMsupplier/evaluationSheet/".concat(evaluationNo, "/items/").concat(supplierCode),
    method: 'get'
  });
} // 日常评价单删除

export function deleteRecord(rowId) {
  return request({
    url: "/SRMsupplier/evaluationSheet/".concat(rowId),
    method: 'delete'
  });
} // 查询单个评价项目

export function queryOneItem(rowId) {
  return request({
    url: "/SRMsupplier/evaluationSheet/".concat(rowId, "/details"),
    method: 'get'
  });
} //  任务发布/综合评价/评价发布

export function submitRecord(rowId, data) {
  return request({
    url: "/SRMsupplier/evaluationSheet/".concat(rowId, "/sheet"),
    method: 'patch',
    data: data
  });
} // 获取评价计划号

export function getPlanNo(query) {
  return request({
    url: "/SRMsupplier/supplierDailyEvaluation/plan",
    method: 'get',
    params: query
  });
}
