import request from '@/utils/request'; // 查询检验记录

export function queryRecords(query) {
  return request({
    url: '/easDevice/inspection/inspect/record',
    method: 'get',
    params: query
  });
} // 创建检验记录

export function addRecords(data) {
  return request({
    url: '/easDevice/inspection/inspect/record',
    method: 'post',
    data: data
  });
} // 更改检验记录状态

export function patchRecords(inspectOrderCode, query) {
  return request({
    url: "/easDevice/inspection/inspect/".concat(inspectOrderCode, "/record/state"),
    method: 'patch',
    params: query
  });
} // 创建检验记录

export function deleteRecords(inspectOrderCode) {
  return request({
    url: "/easDevice/inspection/inspect/".concat(inspectOrderCode, "/record"),
    method: 'delete'
  });
} // 查询检验记录明细

export function queryDetail(query, inspectOrderCode) {
  return request({
    url: "/easDevice/inspection/inspect/".concat(inspectOrderCode, "/details"),
    method: 'get',
    params: query
  });
}
