import request from '@/utils/request'; // 查询项目信息列表

export function getProject() {
  return request({
    url: "iotIot/iot/project",
    method: 'get'
  });
} // 查询设备类型

export function getDeviceType(projectCode) {
  return request({
    url: "iotIot/iot/dataQuery/".concat(projectCode, "/deviceType"),
    method: 'get'
  });
} // 查询设备名称

export function getDevice(projectCode, typeCode) {
  return request({
    url: "iotIot/iot/dataQuery/".concat(projectCode, "/").concat(typeCode, "/device"),
    method: 'get'
  });
} // 查询设备点位

export function postPoint(data) {
  return request({
    url: "iotIot/iot/dataQuery/point",
    method: 'post',
    data: data
  });
} // 数据查询

export function postDataQuery(data, query) {
  return request({
    url: "iotIot/iot/dataQuery",
    method: 'post',
    data: data,
    params: query
  });
}
