import request from '@/utils/request'; // 查询

export function queryDataItem(query) {
  return request({
    url: "/ems/dataItem/dataItemList",
    method: 'get',
    params: query
  });
} // 查询单个

export function queryFlatWindItem(rowId) {
  return request({
    url: "/ems/dtPeriodPriceMaintain/get/".concat(rowId),
    method: 'get'
  });
} // 新增

export function createDataItem(data) {
  return request({
    url: "/ems/dataItem/dataItemSave",
    method: 'post',
    data: data
  });
} // 更新

export function updateDataItem(code, data) {
  return request({
    url: "/ems/dataItem/dataItemUpdate/".concat(code),
    method: 'patch',
    data: data
  });
} // 删除

export function deleteDataItem(data) {
  return request({
    url: "/ems/dataItem/dataItemDelete",
    method: 'delete',
    data: data
  });
}
