import request from '@/utils/request'; //新品物料维护查询

export function queryRecords(query) {
  return request({
    url: "/purchase/newMaterial",
    method: 'get',
    params: query
  });
} //新品物料维护（新增）

export function addRecord(data) {
  return request({
    url: "/purchase/newMaterial",
    method: 'post',
    data: data
  });
} //新品物料维护（编辑）

export function updateRecord(rowId, data) {
  return request({
    url: "/purchase/newMaterial/".concat(rowId),
    method: 'patch',
    data: data
  });
} //新品物料维护维护删除

export function deleteRecord(rowId) {
  return request({
    url: "/purchase/newMaterial/".concat(rowId),
    method: 'delete'
  });
} //新品物料维护（打包取消打包发布）

export function publish(data) {
  return request({
    url: "/purchase/newMaterial/package",
    method: 'patch',
    data: data
  });
}
