import request from '@/utils/request'; // 查询项目信息列表

export function getDevice(query) {
  return request({
    url: "iotIot/iot/dataQuery/device",
    method: 'get',
    params: query
  });
} // 设备数据查询详情实时数据

export function getDeviceDate(projectCode, gatewayCode, deviceCode, query) {
  return request({
    url: "iotIot/iot/dataQuery/".concat(projectCode, "/device/").concat(gatewayCode, "/").concat(deviceCode, "/realTimeDate"),
    method: 'get',
    params: query
  });
}
