import { queryDeviceBinds, updateDeliver } from "@/api/project/iot/IOT/db";
export default {
  name: "DBTimeData",
  props: {
    itemData: {
      type: Object,
      default: undefined
    },
    code: {
      type: String,
      default: undefined
    }
  },
  watch: {
    itemData: function itemData() {
      this.getList();
    }
  },
  data: function data() {
    return {
      listLoading: false,
      theight: 0,
      //表格高度
      list: [],
      tableKey: 0,
      //表格索引
      listQuery: {
        time: undefined
      },
      dialogFormVisible: false,
      chartList: [{
        yers: '2010',
        value: 2500,
        name: '出口1'
      }, {
        yers: '2011',
        value: 4500,
        name: '出口3'
      }, {
        yers: '2012',
        value: 3500,
        name: '出口4'
      }, {
        yers: '2013',
        value: 4500,
        name: '出口5'
      }]
    };
  },
  mounted: function mounted() {
    var _this = this;

    this.setTableHeight(); //表格高度自适应

    window.onresize = function () {
      _this.setTableHeight();
    };
  },
  methods: {
    //获取表格数据
    getList: function getList() {
      var _this2 = this;

      this.listLoading = true;
      queryDeviceBinds(this.code, this.itemData.gatewayCode, this.itemData.deviceCode).then(function (response) {
        _this2.$emit('getSubList', response.data);

        _this2.list = response.data;
        setTimeout(function () {
          _this2.listLoading = false;
        }, 1.5 * 100);
      });
    },
    // 交付
    pay: function pay(pointCode) {
      var _this3 = this;

      this.$confirm('此操作将交付所选中数据, 是否继续?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function () {
        updateDeliver(_this3.code, _this3.itemData.gatewayCode, _this3.itemData.deviceCode, pointCode).then(function () {
          _this3.getList();

          _this3.$message({
            title: '成功',
            message: '交付成功',
            type: 'success',
            duration: 2000
          });
        });
      }).catch(function () {
        _this3.$message({
          type: 'info',
          message: '已取消交付'
        });
      });
    },
    // 查看曲线
    handleViewCurves: function handleViewCurves() {
      return this.$message({
        title: this.$t('message.warning'),
        message: "此功能正在进行中，敬请期待！",
        type: "warning"
      }); // this.dialogFormVisible = true
      // this.drawLine()
    },
    handleQuery: function handleQuery() {},
    drawLine: function drawLine() {
      // 基于准备好的dom，初始化echarts实例
      var myChart = this.$echarts.init(this.$refs.myChart); // 绘制图表

      myChart.setOption({
        title: {
          text: '空压机'
        },
        xAxis: {
          data: this.chartList.map(function (item) {
            return item.yers;
          })
        },
        yAxis: {},
        series: [{
          name: '空压机',
          type: 'line',
          data: this.chartList
        }]
      });
    },
    //表格高度计算
    setTableHeight: function setTableHeight() {
      this.theight = this.$myFun.getSingleTbHeight() - 30;
    }
  }
};
