import request from '@/utils/request'; //二元化清单查询

export function queryRecords(query) {
  return request({
    url: "/purchase/binaryMaterial",
    method: 'get',
    params: query
  });
} //二元化清单（新增）

export function addRecord(data) {
  return request({
    url: "/purchase/binaryMaterial",
    method: 'post',
    data: data
  });
} //二元化清单（编辑）

export function updateRecord(data) {
  return request({
    url: "/purchase/binaryMaterial",
    method: 'patch',
    data: data
  });
} //二元化清单删除

export function deleteRecord(rowId) {
  return request({
    url: "/purchase/binaryMaterial/".concat(rowId),
    method: 'delete'
  });
} //二元化清单（提交）

export function submitRecord(data) {
  return request({
    url: "/purchase/binaryMaterial/state",
    method: 'patch',
    data: data
  });
}
