import request from '@/utils/request'; //分工规则定义主表查询

export function queryRecords(query) {
  return request({
    url: "/purchase/divisionRule",
    method: 'get',
    params: query
  });
} //分工规则定义主表（新增）

export function addRecord(data) {
  return request({
    url: "/purchase/divisionRule",
    method: 'post',
    data: data
  });
} //分工规则定义主表（编辑）

export function updateRecord(rowId, data) {
  return request({
    url: "/purchase/divisionRule/".concat(rowId),
    method: 'patch',
    data: data
  });
} //分工规则定义主表删除

export function deleteRecord(rowId) {
  return request({
    url: "/purchase/divisionRule/".concat(rowId),
    method: 'delete'
  });
} //分工规则定义从表查询

export function queryRecordSub(query) {
  return request({
    url: "/purchase/divisionRule/".concat(query.rowId, "/item"),
    method: 'get',
    params: query
  });
} //分工规则定义从表（新增）

export function addRecordSub(rowId, data) {
  return request({
    url: "/purchase/divisionRule/".concat(rowId, "/item"),
    method: 'post',
    data: data
  });
} //分工规则定义从表（编辑）

export function updateRecordSub(ruleId, rowId, data) {
  return request({
    url: "/purchase/divisionRule/".concat(ruleId, "/item/").concat(rowId),
    method: 'patch',
    data: data
  });
} //分工规则定义从表删除

export function deleteRecordSub(ruleId, rowId) {
  return request({
    url: "/purchase/divisionRule/".concat(ruleId, "/item/").concat(rowId),
    method: 'delete'
  });
}
