import request from '@/utils/request'; //备件领用申请主信息查询

export function queryRecords(query) {
  return request({
    url: "/iwms/plans/useOrders",
    method: 'get',
    params: query
  });
} //备件领用申请明细信息查询

export function queryRecordItems(useOrderCode, query) {
  return request({
    url: "/iwms/plans/useOrders/".concat(useOrderCode, "/details"),
    method: 'get',
    params: query
  });
} //查询单个领用申请单

export function queryRecordone(useOrderCode) {
  return request({
    url: "/iwms/plans/useOrders/".concat(useOrderCode),
    method: 'get'
  });
} //备件领用申请新增

export function addRecords(data) {
  return request({
    url: "/iwms/plans/useOrders",
    method: 'post',
    data: data
  });
} //备件领用申请（编辑）

export function updateRecord(useOrderCode, data) {
  return request({
    url: "/iwms/plans/useOrders/".concat(useOrderCode),
    method: 'patch',
    data: data
  });
} //备件领用申请删除

export function deleteRecord(useOrderCode) {
  return request({
    url: "/iwms/plans/useOrders/".concat(useOrderCode),
    method: 'delete'
  });
}
