import request from '@/utils/request';
export function queryRecords(query) {
  return request({
    url: "/easDevice/ledger/run/record",
    method: 'get',
    params: query
  });
} // 查询单条

export function queryRecord(runRecordCode) {
  return request({
    url: "/easDevice/ledger/run/record/".concat(runRecordCode),
    method: 'get'
  });
}
export function addRecords(data) {
  //新增
  return request({
    url: "/easDevice/ledger/run/record",
    method: 'post',
    data: data
  });
}
export function updataRecord(runRecordCode, data) {
  //修改
  return request({
    url: "/easDevice/ledger/run/record/".concat(runRecordCode),
    method: 'patch',
    data: data
  });
}
export function deleteRecord(runRecordCode) {
  //删除
  return request({
    url: "/easDevice/ledger/run/record/".concat(runRecordCode),
    method: 'delete'
  });
} //  查询设备运行记录单明细列表

export function queryRecordsSub(query, runRecordCode) {
  return request({
    url: "/easDevice/ledger/run/record/".concat(runRecordCode, "/details"),
    method: 'get',
    params: query
  });
} //  查询单个

export function queryRecordSub(runRecordCode, rowNo) {
  return request({
    url: "/easDevice/ledger/run/record/".concat(runRecordCode, "/details/").concat(rowNo),
    method: 'get'
  });
}
