  //新增
  return request({
    url: "/easDevice/ledger/run/record/".concat(runRecordCode, "/details"),
    method: 'post',
    data: data
  });
}
export function updataRecordSub(runRecordCode, rowNo, data) {
  //修改
  return request({
    url: "/easDevice/ledger/run/record/".concat(runRecordCode, "/details/").concat(rowNo),
    method: 'patch',
    data: data
  });
} //

export function deleteRecordSub(runRecordCode, rowNos) {
  //删除
  return request({
    url: "/easDevice/ledger/run/record/".concat(runRecordCode, "/details/").concat(rowNos),
