import request from '@/utils/request'; // 查询备件请购列表

export function queryRecords(query) {
  return request({
    url: '/spm/requisitions',
    method: 'get',
    params: query
  });
} // 查询备件请购列表

export function addRecords(data) {
  return request({
    url: '/spm/requisitions',
    method: 'post',
    data: data
  });
} // 修改备件请购

export function patchRecords(requisitionsNumber, data) {
  return request({
    url: "/spm/requisitions/".concat(requisitionsNumber),
    method: 'patch',
    data: data
  });
} // 提交备件请购

export function submitRecords(requisitionsNumber) {
  return request({
    url: "/spm/requisitions/submit",
    method: 'patch',
    data: [requisitionsNumber]
  });
} // 删除备件请购

export function deleteRecords(requisitionsNumbers) {
  return request({
    url: "/spm/requisitions",
    method: 'delete',
    data: requisitionsNumbers // headers: {
    //     'Content-Type': 'application/json',
    // },

  });
} // 查询备件请购明细

export function queryDetails(query) {
  return request({
    url: '/spm/requisitions/detail',
    method: 'get',
    params: query
  });
}
