import request from '@/utils/request'; //查询保养工单

export function queryRecords(query) {
  return request({
    url: '/easDevice/upkeep/work/order',
    method: 'get',
    params: query
  });
} //新增保养工单

export function addRecord(data) {
  return request({
    url: "/easDevice/upkeep/work/order",
    method: 'post',
    data: data
  });
} //修改保养工单

export function patchRecord(workOrderNumber, data) {
  return request({
    url: "/easDevice/upkeep/work/order/".concat(workOrderNumber),
    method: 'patch',
    data: data
  });
} //删除保养工单

export function deleteRecord(ids) {
  return request({
    url: '/easDevice/upkeep/work/order',
    method: 'delete',
    data: ids
  });
} // 保养工单派工

export function dispatchRecord(ids) {
  return request({
    url: '/easDevice/upkeep/work/order/ISSUE/state',
    method: 'patch',
    data: ids
  });
} //查询保养工单实体

export function queryRecordsDetail(workOrderNumber) {
  return request({
    url: "/easDevice/upkeep/work/order/".concat(workOrderNumber),
    method: 'get'
  });
} //查询保养工备件

export function queryParts(workOrderNumber) {
  return request({
    url: '/easDevice/maintain/upkeep/basics/sp',
    method: 'get',
    params: {
      upkeepWorkOrderNumber: workOrderNumber
    }
  });
} //查询保养工部位列表

export function queryDetails(query) {
  return request({
    url: "/easDevice/upkeep/work/order/position",
    method: 'get',
    params: query
  });
} //查询单个点检计划

export function querySingleRecords(inspectPlanCode, deviceCode) {
  return request({
    url: "/easDevice/inspection/inspect/".concat(inspectPlanCode, "/plan/").concat(deviceCode),
    method: 'get'
  });
} //查询单个点检计划-点检

export function querySingleRecord(inspectPlanCode, deviceCode) {
  return request({
    url: "/easDevice/inspection/inspect/".concat(inspectPlanCode, "/plan/").concat(deviceCode, "/inspect"),
    method: 'get'
  });
} //修改单个点检计划

export function patchSingleRecords(inspectPlanCode, deviceCode, data) {
  return request({
    url: "/easDevice/inspection/inspect/".concat(inspectPlanCode, "/plan/").concat(deviceCode),
    method: 'patch',
    data: data
  });
} //删除点检计划

export function deleteRecords(data) {
  return request({
    url: '/easDevice/inspection/inspect',
    method: 'post',
    data: data
  });
}
