import request from '@/utils/request'; //查询维修工单列表

export function queryRecords(query) {
  return request({
    url: "/easDevice/maintain/workOrder/",
    method: 'get',
    params: query
  });
} //根据维修工单号查询维修工单

export function detailsRecords(workOrderNumber) {
  return request({
    url: "/easDevice/maintain/workOrder/".concat(workOrderNumber),
    method: 'get'
  });
} //新增维修工单

export function addRecords(data) {
  return request({
    url: "/easDevice/maintain/workOrder/",
    method: 'post',
    data: data
  });
} //拒单

export function refuseRecords(workOrderNumber, data) {
  return request({
    url: "/easDevice/maintain/workOrder/refuse/".concat(workOrderNumber),
    method: 'patch',
    data: data
  });
} //登到

