import * as api from '@/api/project/mes/dataBase/stationEquipment/stationEquipmentSelect';
import Pagination from '@/components/Pagination';
export default {
  name: 'stationEquipmentSelect',
  components: {
    Pagination: Pagination
  },
  data: function data() {
    return {
      list: null,
      total: 0,
      listQuery: {
        currentPage: 1,
        pageSize: 10
      },
      activeName2: 'first',
      listSelected: [],
      tableKey: 0,
      allowSelect: false
    };
  },
  mounted: function mounted() {
    this.getEquipmentData();
  },
  methods: {
    checked: function checked(i) {
      this.$refs.tb_bx.toggleRowSelection(this.list[i], true);
    },
    handleQuery: function handleQuery() {
      this.listQuery.currentPage = 1;
      this.getEquipmentData();
      this.listSelected = [];
    },
    // 父级工位表格展示已选，modal已选勾选
    getListSelected: function getListSelected() {
      this.listSelected = this.$parent.$parent.$parent.list_b;
    },
    //检查是否取消勾选
    checkUnsel: function checkUnsel(val, row) {
      var isuncheck = true;
      val.forEach(function (item) {
        if (item.equipmentCode === row.equipmentCode) {
          isuncheck = false;
        }
      });
      return isuncheck;
    },
    selectMain: function selectMain(val, row) {
      var _this = this;

      var isuncheck = this.checkUnsel(val, row);

      if (!isuncheck) {
        if (this.listSelected) {
          val.forEach(function (items, indexv) {
            var isSelected = false;

            _this.listSelected.forEach(function (item) {
              if (item.equipmentCode === items.equipmentCode) {
                isSelected = true;
              }
            });

            if (!isSelected) {
              _this.listSelected.push(val[indexv]);
            }
          });
        } else {
          this.listSelected = val;
        }
      } else {
        this.listSelected = this.listSelected.filter(function (item) {
          return item.equipmentCode !== row.equipmentCode;
        });
      }
    },
    selectAll: function selectAll(val) {
      var _this2 = this;

      if (val.length == 0) {
        this.list.forEach(function (item) {
          _this2.listSelected.forEach(function (item2, index) {
            if (item.equipmentCode == item2.equipmentCode) {
              _this2.listSelected.splice(index, 1);
            }
          });
        });
        return false;
      }

      val.forEach(function (items, indexv) {
        var isSelected = false;

        _this2.listSelected.forEach(function (item) {
          if (item.equipmentCode === items.equipmentCode) {
            isSelected = true;
          }
        });

        if (!isSelected) {
          _this2.listSelected.push(val[indexv]);
        }
      });
    },
    // 在备选中把已选打钩
    getEquipmentData: function getEquipmentData() {
      var _this3 = this;

      api.getEquipmentAll(this.listQuery).then(function (res) {
        res = res.data;
        _this3.list = res.list;
        _this3.total = res.pages.count;

        if (_this3.listSelected) {
          _this3.listSelected.forEach(function (items) {
            _this3.list.forEach(function (item, index) {
              if (item.equipmentCode === items.equipmentCode) {
                _this3.$nextTick(function () {
                  this.checked(index);
                });
              }
            });
          });
        }
      });
    }
  }
};
