import request from '@/utils/request'; // 查询设备类型

export function queryOcation(query) {
  return request({
    url: "/easDevice/allocation",
    method: 'get',
    params: query
  });
} // allocation 查询设备调拨列表

export function queryAllocation(query) {
  return request({
    url: "/easDevice/allocation",
    method: 'get',
    params: query
  });
} // 查询单个设备调拨

export function queryAllocations(allocationApplyCode) {
  return request({
    url: "/easDevice/allocation/".concat(allocationApplyCode),
    method: 'get'
  });
} //
// 查询设备请购单明细列表

export function queryAllocationsDetails(allocationApplyCode) {
  return request({
    url: "/easDevice/allocation/".concat(allocationApplyCode, "/details"),
    method: 'get'
  });
} //更新设备调拨

export function updataAllocation(allocationApplyCode, data) {
  return request({
    url: "/easDevice/allocation/".concat(allocationApplyCode),
    method: 'patch',
    data: data
  });
} // 删除调拨

export function deleteAllocation(allocationApplyCode) {
  return request({
    url: "/easDevice/allocation/".concat(allocationApplyCode),
    method: 'delete'
  });
} // 提交调拨

export function updataAllocationState(allocationApplyCode) {
  var data = {
    method: 'SUBMIT'
  };
  return request({
    url: "/easDevice/allocation/".concat(allocationApplyCode, "/state"),
    method: 'patch',
    params: data
  });
} // 查询工作中心

export function queryWork(orgCode) {
  var query = {
    orgCode: orgCode
  };
  return request({
    url: "/easDevice/work-centers",
    method: 'get',
    params: query
  });
} // 查询工位列表

export function queryStations(workCenterCode) {
  var query = {
    workCenterCode: workCenterCode
  };
  return request({
    url: "/easDevice/work-centers/".concat(workCenterCode, "/stations"),
    method: 'get',
    params: query
  });
} // 查询设备台账信息

export function queryDevice(query) {
  return request({
    url: "/easDevice/file/device",
    method: 'get',
    params: query
  });
} // 新增设备调拨

