import request from '@/utils/request'; // 查询设备部件信息

export function querypart(typeCode) {
  return request({
    url: "/easDevice/base/part/".concat(typeCode),
    method: 'get'
  });
} // 查询设备故障信息

export function queryRecords(query) {
  return request({
    url: "/easDevice/fault/register",
    method: 'get',
    params: query
  });
} // 新增设备故障信息

export function addRecords(data) {
  return request({
    url: "/easDevice/fault/register",
    method: 'post',
    data: data
  });
} // 查询设备类型

export function queryTypeCode(query) {
  return request({
    url: "/easDevice/base/deviceType",
    method: 'get',
    params: query
  });
} // 修改设备故障信息

export function updateRecords(faultNumber, data) {
  return request({
    url: "/easDevice/fault/register/".concat(faultNumber),
    method: 'patch',
    data: data
  });
} // 根据编号获取对象

export function numberRecords(faultNumber) {
  return request({
    url: "/easDevice/fault/register/".concat(faultNumber),
    method: 'get'
  });
} // 批量删除设备故障信息

export function deleteRecords(data) {
  return request({
    url: "/easDevice/fault/register",
    method: 'delete',
    data: data
  });
} // 报修设备故障信息

export function repairsRecords(data) {
  return request({
    url: "/easDevice/fault/register/repairs",
    method: 'patch',
    data: data
  });
} // 查询设备故障处理记录列表

export function detailsRecords(faultNumber) {
  return request({
    url: "/easDevice/fault/register/disposeLog?faultNumber=".concat(faultNumber),
    method: 'get'
  });
} // 查询故障代码列表

export function queryFaultCode(query) {
  return request({
    url: "/easDevice/base/fault/code",
    method: 'get',
    params: query
  });
}
