// 入平台提案api
import request from '@/utils/request'; //查询入平台提案列表

export function queryRecords(query) {
  return request({
    url: "/SRMsupplier/proposals",
    method: 'get',
    params: query
  });
} //查询单个入平台提案

export function queryRow(proposalNo) {
  return request({
    url: "/SRMsupplier/proposals/".concat(proposalNo),
    method: 'get'
  });
} //查询入平台提案列表

export function queryItem(query) {
  return request({
    url: "/SRMsupplier/proposals/".concat(query.proposalNo, "/items"),
    method: 'get',
    params: query
  });
} // 更新、发布入平台提案

export function updateRecords(data) {
  return request({
    url: "/SRMsupplier/proposals/".concat(data.proposalDTO.proposalNo),
    method: 'patch',
    data: data
  });
}
export function publish(proposalNo) {
  return request({
    url: "/SRMsupplier/proposals/".concat(proposalNo),
    method: 'patch',
    data: {
      method: 'RELEASE'
    }
  });
} // 新增平台提案

export function createRecords(data) {
  return request({
    url: "/SRMsupplier/proposals",
    method: 'post',
    data: data
  });
} // 删除平台提案

export function deleteRecords(proposalNo) {
  return request({
    url: "/SRMsupplier/proposals/".concat(proposalNo),
    method: 'delete'
  });
}
