import dialogWlxzbyzsj from '@/components/Dialog/dialogMaterial.vue'; //主数据物料选择

import dialogMaterielCategory from '@/components/Dialog/dialogMaterielCategory.vue'; //物料分类

import throttle from 'lodash/throttle';
export default {
  components: {
    caiGou: caiGou,
    dialogPersonSelect: dialogPersonSelect,
    dialogWlxzbyzsj: dialogWlxzbyzsj,
    dialogMaterielCategory: dialogMaterielCategory
  },
  data: function data() {
    return {
      dialogStatus: "",
      dialogVisible: false,
      person: "",
      DICT_CODE: DICT_CODE,
      dialogPersonVisible: false,
      titlePerson: "",
      temp: {
        rowId: undefined
      },
      rules: {
        orgCode: [{
          required: true,
          message: '组织编码不能为空',
          trigger: 'blur'
        }],
        parentRowId: [{
          required: true,
          message: '上级分类编码不能为空',
          trigger: 'blur'
        }],
        classCode: [{
          required: true,
          message: '分类编码不能为空',
          trigger: 'blur'
        }],
