      return state.dict.dt_manufacture_diff;
    },
    dt_srm_pdy: function dt_srm_pdy(state) {
      return state.dict.dt_srm_pdy;
    },
    dt_unit_type: function dt_unit_type(state) {
      return state.dict.dt_unit_type;
    },
    dt_timeunit: function dt_timeunit(state) {
      return state.dict.dt_timeunit;
    },
    dt_srm_pdz: function dt_srm_pdz(state) {
      return state.dict.dt_srm_pdz;
    },
    dt_srm_pc0: function dt_srm_pc0(state) {
      return state.dict.dt_srm_pc0;
    }
  })),
  mounted: function mounted() {
    this.$store.dispatch("dict/getDicData", ["dt_manufacture_diff", 'dt_srm_pdy', 'dt_unit_type', 'dt_timeunit', 'dt_srm_pdz', 'dt_srm_pc0']);
  },
