    //物料选择
    handleMaterialCode: function handleMaterialCode() {
      this.$refs.refZsjWlxz.dialogVisible = true;
    },
    //物料选择确定
    confirmMateriel: function confirmMateriel(data) {
      console.log(data, 'bbbvvv');
      this.$set(this.temp, 'materialCode', data[0].materielCode);
      this.$set(this.temp, 'materialName', data[0].materielName);
      this.$set(this.temp, 'materielEngName', data[0].materielEngName); //物料英文名

      this.$set(this.temp, 'materielType', data[0].materielType); //物料类型

      this.$set(this.temp, 'materielTypeName', data[0].materielTypeName); //物料类型名称

      this.$set(this.temp, 'materielGroup', data[0].materielGroup); //物料组

      this.$set(this.temp, 'materielGroupName', data[0].materielGroupName); //物料组名称
    },
    //物料分类
    handleClassifyCode: function handleClassifyCode() {
      if (this.temp.purchaseOrg) {
        this.$refs.wlfl.listQuery.orgCode = this.temp.purchaseOrg;
        this.$refs.wlfl.selectedRows = [];
        this.$refs.wlfl.handleQuery();
        this.$refs.wlfl.dialogVisible = true;
      } else {
        this.$message({
          type: 'warning',
          message: '请先选择采购组织'
        });
      }
    },
    //物料分类选择确定
    confirmClassify: function confirmClassify(data) {
      this.$set(this.temp, 'materialClassification', data[0].classCode);
      this.$set(this.temp, 'materialClassificationName', data[0].className);
    },
    //人员选择
    handlePersonCode: function handlePersonCode(val) {
      this.person = val;

      if (this.person == 'jhPerson') {
        this.titlePerson = '采购计划员';
      } else if (this.person == 'glPerson') {
        this.titlePerson = '采购管理员';
      } else {
        this.titlePerson = '商务人员';
      }

      this.$refs.person.dialogVisible = true;
    },
    //人员选择确定
    personConfirm: function personConfirm(val) {
      console.log(val[0].employeeName);

      if (this.titlePerson === '采购计划员') {
        this.$set(this.temp, 'purchasePlanner', val[0].employeeCode);
        this.$set(this.temp, 'purchasePlannerName', val[0].employeeName);
      }

      if (this.titlePerson === '采购管理员') {
        this.$set(this.temp, 'purchaseManager', val[0].employeeCode);
        this.$set(this.temp, 'purchaseManagerName', val[0].employeeName);
      }

      if (this.titlePerson === '商务人员') {
        this.$set(this.temp, 'purchaseBusiness', val[0].employeeCode);
        this.$set(this.temp, 'purchaseBusinessName', val[0].employeeName);
      }
    },
    //获取组织编码名称
    selectName: function selectName(val) {
      this.temp.purchaseOrgName = val;
    },
    //获取采购组名称
    selectorgGroupName: function selectorgGroupName(val) {
      this.temp.purchaseGroupName = val;
    },
