        if (valid) {
          api.addRecord(_this.temp).then(function () {
            _this.$message({
              title: '成功',
              message: '新增成功',
              type: 'success',
              duration: 2000
            });

            _this.dialogVisible = false;

            _this.$emit('refresh');
          });
        } else {
          return false;
        }
      });
    }, 2000, {
      leading: true,
      trailing: false
    }),
    updateData: throttle(function () {
      var _this2 = this;

      api.updateRecord(this.temp.rowId, this.temp).then(function () {
        _this2.$message({
          title: '成功',
          message: '编辑成功',
          type: 'success',
          duration: 2000
        });

        _this2.dialogVisible = false;

        _this2.$emit('refresh');
      });
    }, 2000, {
      leading: true,
      trailing: false
    }),
