      this.temp = {
        purchaseOrg: undefined,
        materialCode: undefined,
        materialName: undefined,
        makeDistinction: undefined,
        purchaseManagerName: undefined,
        purchaseGroup: undefined,
        purchasePlannerName: undefined,
        purchaseBusinessName: undefined,
        materialClassificationName: undefined,
        targetPrice: undefined,
        purchaseClassification: undefined,
        purchaseUmo: undefined,
        basicUmo: undefined,
        purchaseMeasurementDistribution: undefined,
        basicMeasurementDistribution: undefined,
        purchaseSnp: undefined,
        purchaseLeadTime: undefined,
        eoq: undefined,
        leadTimeUnit: undefined,
        purchaseTriggeringMethod: undefined,
        purchaseStrategy: undefined
      };
    }
  }
};
