// 选择方案编制api
import request from '@/utils/request'; //查询采购方案列表

export function queryRecords(query) {
  return request({
    url: '/business/purchase-plans',
    method: 'get',
    params: query
  });
} //查询单个采购方案

export function queryRow(planNo) {
  return request({
    url: "/business/purchase-plans/".concat(planNo),
    method: 'get'
  });
} //查询采购方案的物料和供应商

export function queryItem(planNo) {
  return request({
    url: "/business/purchase-plans/".concat(planNo, "/items"),
    method: 'get'
  });
} // 更新、提交、发布采购方案

export function updateRecords(data) {
  return request({
    url: "/business/purchase-plans/handle",
    method: 'patch',
    data: data
  });
} // 创建采购方案

export function createRecords(data) {
  return request({
    url: '/business/purchase-plans',
    method: 'post',
    data: data
  });
} // 删除采购方案

export function deleteRecords(planNo) {
  return request({
    url: "/business/purchase-plans/".concat(planNo),
    method: 'delete'
  });
} // 分工、发布、驳回、分类设置、打包、取消打包

export function handleOprate(data) {
  return request({
    url: "/purchase/division/handle",
    method: 'patch',
    data: data
  });
} //异常反馈
