export default {
  name: "pie",
  props: ['id', 'data', 'title', 'radius', 'roseType', 'center', 'label', 'itemStyle'],
  data: function data() {
    return {
      mycharts: undefined
    };
  },
  watch: {
    data: function data() {
      this.getPie();
    }
  },
  mounted: function mounted() {
    var _this = this;

    window.onresize = function () {
      _this.mycharts.resize();
    };

    this.getPie();
  },
  methods: {
    getPie: function getPie() {
      if (!this.data) return false;
      var name = this.data.name ? this.data.name : '';
      var legend = this.data.legend ? this.data.legend : [];
      this.myChart = this.$echarts.init(document.getElementById(this.id));
      var option = {
        title: {
          text: this.title,
          textStyle: {
            color: '#23b8e8',
            fontSize: '14'
          }
        },
        tooltip: {
          trigger: 'item',
          formatter: '{a} <br/>{b}: {c} ({d}%)',
          confine: true
        },
        legend: {
          orient: 'vertical',
          right: 10,
          data: legend
        },
        series: [{
          name: name,
          type: 'pie',
          radius: this.radius,
          avoidLabelOverlap: false,
          label: {
            show: this.label
          },
          emphasis: {
            label: {
              show: true
            }
          },
          labelLine: {
            show: this.label,
            lineStyle: {
              color: 'rgba(255, 255, 255, 0.7)'
            },
            smooth: 0.2,
            length: 10,
            length2: 20
          },
          data: this.data.data,
          animationType: 'scale',
          animationEasing: 'elasticOut',
          animationDelay: function animationDelay() {
            return Math.random() * 200;
          }
        }]
      };

      if (this.center) {
        option.series[0].center = this.center;
      }

      if (this.itemStyle) {
        option.series[0].itemStyle = this.itemStyle;
      }

      if (this.roseType) {
        option.series[0].roseType = this.roseType;
      }

      this.myChart.setOption(option, true);
    }
  }
};
