import * as api from '@/api/data-base/color';
import Pagination from '@/components/Pagination';
import { parseTime } from '@/utils';
import addKey from "./addKey";
import detail from "./historydetail";
import addUser from "@/components/Select/personSelect.vue";
export default {
  name: 'color',
  components: {
    Pagination: Pagination,
    addKey: addKey,
    addUser: addUser,
    detail: detail
  },
  data: function data() {
    return {
      list: null,
      total: 0,
      theight: 0,
      //表格高度
      start: "",
      end: "",
      listQuery: {
        page: true,
        currentPage: 1,
        pageSize: 15,
        colorCode: undefined,
        colorName: undefined
      },
      temp: {
        colorCode: undefined,
        colorName: undefined,
        colorAbbreviation: undefined,
        state: 1,
        remark: undefined
      },
      mainList: {},
      activeCollNames: ['1'],
      parentsRows: [],
      colorCode: undefined,
      selectCode: [],
      dialogVisible: false,
      dialogVisiblekey: false,
      dialogVisibleperson: false,
      dialogVisibledetail: false,
      dialogStatus: '',
      tableKey: 0
    };
  },
  created: function created() {
    this.getList();
  },
  mounted: function mounted() {
    var _this = this;

    this.setTableHeight(); //表格高度自适应

    window.onresize = function () {
      _this.setTableHeight();
    };

    this.getList();
  },
  methods: {
    getList: function getList() {
      var _this2 = this;

      api.queryRecords(this.listQuery).then(function (res) {
        _this2.list = res.data.list;
        _this2.total = res.data.pages.count;
      });
    },
    //表格高度计算
    setTableHeight: function setTableHeight() {
      this.theight = this.$myFun.getSingleTbHeight();
    },
    handleQuery: function handleQuery() {
      //查询
      this.listQuery.currentPage = 1;
      this.getList();
    },
    handleCreate: function handleCreate() {
      //新增
      this.resetTemp();
      this.dialogStatus = 'create';
      this.dialogVisible = true;
    },
    handleUpdate: function handleUpdate(index, row) {
      console.log(row, '111');
      this.resetTemp();
      this.dialogStatus = 'update';
      this.dialogVisible = true;
      this.mainList = row;
    },
    handleDelete: function handleDelete() {},
    handleDetail: function handleDetail() {
      this.dialogVisibledetail = true;
    },
    handleAddKey: function handleAddKey() {
      this.dialogVisiblekey = true;
    },
    personSelect: function personSelect(row) {
      this.parentsRows = row;
    },
    handleAddUser: function handleAddUser() {
      this.dialogVisibleperson = true;
    },
    // 获取表格选中时的数据
    selectRow: function selectRow(val) {
      this.selectlistRow = val;
    },
    confirmKey: function confirmKey() {
      if (this.$refs.addKey.listSelected.length > 0) {
        this.mainList.organizationCode = this.$refs.addKey.listSelected[0].organizationCode;
        this.dialogVisiblekey = false;
      } else {
        this.mainList.organizationCode = null;
        this.dialogVisiblekey = false;
      }
    },
    confirmUser: function confirmUser() {
      if (!this.parentsRows[0]) {
        this.mainList.organizationName = "";
        this.mainList.organizationName = "";
        this.dialogVisibleperson = false;
      } else {
        this.mainList.organizationName = this.parentsRows[0].employeeName;
        this.dialogVisibleperson = false;
      }
    },
    update: function update() {
      var _this3 = this;

      //编辑提交
      this.$refs['temp'].validate(function (valid) {
        if (valid) {
          api.updateRecord(_this3.temp.colorCode, _this3.temp).then(function () {
            _this3.getList();

            _this3.dialogFormVisible = false;

            _this3.$message({
              title: '成功',
              message: '更新成功',
              type: 'success',
              duration: 2000
            });
          });
        }
      });
    },
    create: function create() {
      var _this4 = this;

      //新增确认按钮
      this.$refs.temp.validate(function (valid) {
        if (valid) {
          api.addRecord(_this4.temp).then(function () {
            _this4.getList();

            _this4.dialogFormVisible = false;

            _this4.$message({
              title: '成功',
              message: '新增成功',
              type: 'success',
              duration: 2000
            });
          });
        } else {
          return false;
        }
      });
    },
    cancel: function cancel() {
      //取消弹窗按钮
      this.dialogFormVisible = false;
    },
    resetTemp: function resetTemp() {
      this.temp = {
        colorCode: undefined,
        colorName: undefined,
        colorAbbreviation: undefined,
        state: 1,
        remark: undefined
      };
    },
    handleClose: function handleClose() {
      this.resetTemp();
      this.$refs.temp.resetFields();
    },
    handleSelectionChange: function handleSelectionChange(selection) {
      this.selectCode = selection.map(function (v) {
        return v.colorCode;
      });
    },
    handleDownload: function handleDownload() {
      var _this5 = this;

      this.downloadLoading = true;
      import('@/vendor/Export2Excel').then(function (excel) {
        var tHeader = ['颜色编码', '颜色名称', '颜色简称', '状态', '备注'];
        var filterVal = ['colorCode', 'colorName', 'colorAbbreviation', 'state', 'remark'];

        var data = _this5.formatJson(filterVal, _this5.list);

        excel.export_json_to_excel({
          header: tHeader,
          data: data,
          filename: 'table-list'
        });
        _this5.downloadLoading = false;
      });
    },
    formatJson: function formatJson(filterVal, jsonData) {
      return jsonData.map(function (v) {
        return filterVal.map(function (j) {
          if (j === 'timestamp') {
            return parseTime(v[j]);
          } else {
            return v[j];
          }
        });
      });
    }
  }
};
