// 准入评价api
import request from '@/utils/request'; //查询查询准入评价列表

export function queryRecords(query) {
  return request({
    url: "/SRMsupplier/evaluations",
    method: 'get',
    params: query
  });
} //查询单个准入评价

export function queryRow(evaluationNo) {
  return request({
    url: "/SRMsupplier/evaluations/".concat(evaluationNo),
    method: 'get'
  });
} //查询单个准入评价项目

export function queryRowItem(evaluationNo, rowId) {
  return request({
    url: "/SRMsupplier/evaluations/".concat(evaluationNo, "/items/").concat(rowId),
    method: 'get'
  });
} // 评分、提交准入评价

export function updateRecords(evaluationNo, data) {
  return request({
    url: "/SRMsupplier/evaluations/".concat(evaluationNo),
    method: 'patch',
    data: data
  });
} //评分、提交准入评价项目

export function updateItemsRecords(evaluationNo, rowId, data) {
  return request({
    url: "/SRMsupplier/evaluations/".concat(evaluationNo, "/items/").concat(rowId),
    method: 'patch',
    data: data
  });
} //查询准入评价项目列表

export function queryItems(query) {
  return request({
    url: "/SRMsupplier/evaluations/items",
    method: 'get',
    params: query
  });
}
