import { queryDeviceAlarm } from "@/api/project/iot/IOT/db";
import Pagination from "@/components/Pagination";
export default {
  name: "warningEvents",
  props: {
    itemData: {
      type: Object,
      default: undefined
    },
    code: {
      type: String,
      default: undefined
    }
  },
  components: {
    Pagination: Pagination
  },
  watch: {
    itemData: function itemData(val) {
      if (val) {
        this.getList();
      }
    }
  },
  data: function data() {
    return {
      listLoading: false,
      theight: 0,
      //表格高度
      total: 0,
      listQuery: {
        page: true,
        currentPage: 1,
        pageSize: 15
      },
      list: [],
      tableKey: 0 //表格索引

    };
  },
  mounted: function mounted() {
    var _this = this;

    this.setTableHeight(); //表格高度自适应

    window.onresize = function () {
      _this.setTableHeight();
    };
  },
  methods: {
    //获取表格数据
    getList: function getList() {
      var _this2 = this;

      this.listLoading = true;
      queryDeviceAlarm(this.code, this.itemData.gatewayCode, this.itemData.deviceCode, this.listQuery).then(function (response) {
        _this2.list = response.data.list;
        _this2.total = response.data.pages.count;
        setTimeout(function () {
          _this2.listLoading = false;
        }, 1.5 * 100);
      });
    },
    // 支付
    pay: function pay() {},
    // 查看曲线
    handleViewCurves: function handleViewCurves() {},
    //表格高度计算
    setTableHeight: function setTableHeight() {
      this.theight = this.$myFun.getSingleTbHeight() - 30;
    }
  }
};
