import request from '@/utils/request'; //项目主数据查询

export function queryRecords(query) {
  return request({
    url: "/newproduct/newproduct",
    method: 'get',
    params: query
  });
} // 项目主数据新增

export function createRecords(data) {
  return request({
    url: "/newproduct/newproduct",
    method: 'post',
    data: data
  });
} // 项目主数据编辑

export function updateRecords(rowId, data) {
  return request({
    url: "/newproduct/newproduct/".concat(rowId),
    method: 'patch',
    data: data
  });
} //项目主数据删除

export function deleteRecord(data) {
  return request({
    url: "/newproduct/newproduct/delete",
    method: 'delete',
    data: data
  });
} // 项目主数据提交

export function submitRecords(data) {
  return request({
    url: "/newproduct/newproduct/commit",
    method: 'patch',
    data: data
  });
} // 项目主数据关闭

export function closeRecords(data) {
  return request({
    url: "/newproduct/newproduct/close",
    method: 'patch',
    data: data
  });
}
