import request from '@/utils/request'; //合同模板维护查询

export function queryRecords(query) {
  return request({
    url: "/business/contract-template",
    method: 'get',
    params: query
  });
} //合同模板维护（新增）

export function addRecord(data) {
  return request({
    url: "/business/contract-template",
    method: 'post',
    data: data
  });
} //合同模板维护（编辑）

export function updateRecord(rowId, data) {
  return request({
    url: "/business/contract-template/".concat(rowId),
    method: 'patch',
    data: data
  });
} //合同模板维护删除

export function deleteRecord(rowId) {
  return request({
    url: "/business/contract-template/".concat(rowId),
    method: 'delete'
  });
} //合同模板维护明细查询

export function queryRecordsItem(query) {
  return request({
    url: "/business/contract-template/".concat(query.rowId, "/item"),
    method: 'get',
    params: query
  });
} //合同模板维护明细（新增）

