import Pagination from "@/components/Pagination"
import { DICT_CODE, DT_ORG_TYPE, THE_STATE_OPTIONS } from "@/utils/constant"
import { mapState } from "vuex"
import warehouseSelect from "@/components/Select/warehouse-select.vue" //下拉组件
import AuthoType from "@/components/Select/AuthoType.vue" //权限类型下拉组件
import * as api from "@/api/project/wms/stokMove/allocationIndicate"
import wlxzbykcz from '@/components/Select/wlxzbykcz'//库存帐物料信息模块
import orgSelect from "@/components/Select/orgSelect.vue"
export default {
    name: "allocationIndicate",
    components: {
        Pagination,
        AuthoType,
        wlxzbykcz,
        warehouseSelect,
        orgSelect
    },
    data () {
        return {
            theight: 0,
            list: null,
            list1: null,
            list2: [],
            Edit: false,
            unMutiple: false,
            areaList: null,
            locatorList: [],
            toareaList: null,
            tolocatorList: [],
            total: 0,
            total1: 0,
            total2: 0,
            value1: "",
            show: false,
            tkq: "",
            value2: "",
            value3: "",
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                orgCode: undefined,
                productionline: undefined,
                station: undefined,
                feedType: undefined,
                state: undefined,
                batchNumber: undefined,
                leadTime: undefined,
                feedTime: undefined,
                feedwarehouse: undefined,
                workShop: undefined,
                pushTimeStart: undefined,
                pushTimeEnd: undefined,
                pushMaterialState: undefined
            },
            listQuery1: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                supplierCode: undefined,
                orderNumber: undefined
            },
            listQuery2: {
                page: true,
                currentPage: 1,
                pageSize: 10
            },
            listQueryItems: {
                currentPage: 1,
                pageSize: 10,
                allocNumber: undefined
            },
            dto: {
                goodsReadyEmp: undefined,
                goodsReadyEmpName: undefined,
                goodsReadyTime: undefined,
                lineCode: undefined,
                lineName: undefined,
                method: undefined,
                note: undefined,
                orgCode: undefined,
                orgName: undefined,
                pushEmp: undefined,
                pushEmpName: undefined,
                pushMaterialType: undefined,
                pushOrderCode: undefined,
                pushOrderDetails: undefined,
                pushTime: undefined,
                warehouseCode: undefined,
                workshopCode: undefined,
                toOrgName: undefined
            },
            warehouseCode: undefined,
            orgCode: undefined,
            listLoading: true,
            isEaid: false,
            isEdit: true,
            parentsRows1: undefined,
            gw: undefined,
            feedArea: undefined,
            temp: {
                allocNumber: undefined,
                remark: undefined,
                businessType: undefined,
                warehouseCode: undefined,
                toOrgCode: undefined,
                orgCode: undefined,
                toWarehouseCode: undefined,
            },
            xzwlDialogFormVisible: undefined,
            blPersonnelDialogFormVisible1: undefined,
            tlPersonnelDialogFormVisible2: undefined,
            rules: {
                orgCode: [{ required: true, message: "请选择", trigger: "blur" }],
                warehouseCode: [{ required: true, message: "请选择", trigger: "blur" }],
                businessType: [{ required: true, message: "请选择", trigger: "blur" }],
                toOrgCode: [{ required: true, message: "请选择", trigger: "blur" }],
                toWarehouseCode: [{ required: true, message: "请选择", trigger: "blur" }],
            },
            // measureCode: undefined,
            orgName: undefined,
            orgName1: undefined,
            selectCode: [],
            dialogFormVisible: false,
            dialogFormVisible1: false,
            employeeDialogFormVisible: false,
            dialogStatus: "",
            tableKey: [0, 1],
            DICT_CODE: DICT_CODE,
            DT_ORG_TYPE: DT_ORG_TYPE,
            THE_STATE_OPTIONS: THE_STATE_OPTIONS
        }
    },
    computed: {
        ...mapState({
            dt_instruction_state: state => state.dict.dt_instruction_state,
            dt_business_type_screenGroupNum_db: state => state.businessComponent.dt_business_type_screenGroupNum_db
        })
    },
    mounted () {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.$store.dispatch("dict/getDicData", [
            "dt_instruction_state"
        ])
        this.$store.dispatch("businessComponent/getBusinessComponentData", [
            "dt_business_type_screenGroupNum_db"
        ])
        this.getList()
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 170
        },
        getLocatorList (warehouseCode, areaCode, i) { //获取储位
            api.getLocator(warehouseCode, areaCode).then(res => {
                res = res.data
                this.$set(this.locatorList, i, res)
                this.$set(this.list1[i], 'wareLocationCode', res[0].locationCode)
                this.$set(this.list1, i, this.list1[i])
            })
        },
        changeAreaSel (areaCode, i) { //获取储位
            this.getLocatorList(this.temp.toWarehouseCode, areaCode, i)
        },
        togetLocatorList (warehouseCode, areaCode, i) { //获取储位
            api.getLocator(warehouseCode, areaCode).then(res => {
                res = res.data
                this.$set(this.tolocatorList, i, res)
                if (res.length !== 0) {
                    this.$set(this.list1[i], 'toWareLocationCode', res[0].locationCode)
                    this.$set(this.list1, i, this.list1[i])
                } else {
                    this.$set(this.list1[i], 'toWareLocationCode', null)
                }
            })
        },
        changeAreaSela (areaCode, i) { //获取储位
            this.getLocatorList(this.selectlistRow.warehouseCode, areaCode, i)
        },
        tochangeAreaSela (areaCode, i) { //获取储位
            this.togetLocatorList(this.selectlistRow.toWarehouseCode, areaCode, i)
        },
        getList () { //查询主表
            if (this.value1) {
                var d1 = this.value1[0]
                var d2 = this.value1[1]
                this.listQuery.startTime = d1
                this.listQuery.endTime = d2
            } else {
                this.listQuery.startTime = ""
                this.listQuery.endTime = ""
            }
            api.queryRecords(this.listQuery).then(res => {
                res = res.data
                this.list = res.list
                this.total = res.pages.count
                this.$nextTick(function () {
                    this.checked()
                })
                if (this.total == 0) {
                    this.list1 = []
                }
            })
        },
        checked () {
            this.$refs.tb.toggleRowSelection(this.list[0], true)//官网方法，重点
        },
        getItemsList () {  //查询订单明细
            this.listQueryItems.allocNumber = this.selectlistRow.allocNumber
            api.getRecord(this.listQueryItems).then(response => {
                response = response.data
                this.list1 = response.list
                this.total1 = response.pages.count
            })
            api.getArea(this.selectlistRow.warehouseCode).then(resa => {
                resa = resa.data
                this.areaList = resa
            })
            api.getArea(this.selectlistRow.toWarehouseCode).then(resa => {
                resa = resa.data
                this.toareaList = resa
            })
        },
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        handleCreate () { //新增
            this.Edit = false
            this.resetTemp()
            this.dialogStatus = "create"
            this.dialogFormVisible = true
            this.list2 = []
        },

        orgname (name) { //获取组织名称
            this.orgName = name
        },
        orgname1 (name) { //获取往来单位名称
            this.orgName1 = name
        },
        create () { //新增
            this.dto = this.temp
            this.dto.orgName = this.orgName
            this.dto.toOrgName = this.orgName1
            this.list2.map(v => {
                v.allocNumber = this.temp.allocNumber
                // v.wareAreaCode=v.wareAreaNo
                // v.wareLocationCode=v.wareLocationNo
            })
            this.dto.allocationItems = this.list2
            api.addRecord(this.dto).then(() => {
                this.getList()
                this.dialogFormVisible = false
                this.$message({
                    title: "成功",
                    message: "新增成功",
                    type: "success",
                    duration: 2000
                })
                this.list2 = []
            })
        },

        handleDelete () { //删除
            if (this.selectlistRow) {
                this.$confirm("此操作将永久删除该文件,是否继续?", "提示", {
                    confirmButtonText: "确定",
                    cancelButtonText: "取消",
                    type: "warning"
                }).then(() => {
                    api.deleteRecord(this.selectlistRow.allocNumber).then(() => {
                        this.getList()
                        this.$message({
                            title: "成功",
                            message: "删除成功",
                            type: "success",
                            duration: 2000
                        })
                    })
                }).catch(() => {
                    this.$message({
                        type: "info",
                        message: "已取消删除"
                    })
                })

            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning",
                    duration: 2000
                })
            }
        },
        handleCreatewl () {//新增物料
            if (this.temp.orgCode && this.temp.warehouseCode && this.temp.businessType && this.temp.toOrgCode && this.temp.toWarehouseCode) {
                this.xzwlDialogFormVisible = true
                this.orgCode = this.temp.orgCode
                this.warehouseCode = this.temp.warehouseCode
                this.$nextTick(() => {
                    this.$refs.wlxzRef.listSelected = [...this.list2]
                    this.list2 = []
                    this.Edit = true
                    this.$refs.wlxzRef.isEdit = true
                    this.$refs.wlxzRef.getList()
                })
            } else {
                this.$message({
                    title: "警告",
                    message: "请先选择组织,仓库,业务类型,往来单位,往来仓库",
                    type: "warning",
                    duration: 2000
                })
            }
            api.getArea(this.temp.toWarehouseCode).then(resa => {
                resa = resa.data
                this.areaList = resa
            })
        },
        materialxz () { //物料选中中弹窗确认按钮
            if (this.$refs.wlxzRef.listSelected.length > 0) {
                this.list2 = this.$refs.wlxzRef.listSelected
                this.xzwlDialogFormVisible = false
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: this.$t("message.checkedplease"),
                    type: "warning"
                })
            }
        },
        handelRefresh () {//刷新
            this.Edit = false
            this.list2 = []
        },
        selectPartRow (rows) { //父子通信
            this.parentsRows = rows
        },
        handleDeleteitems () {//删除物料
            if (this.selectlistRow4.length !== 0) {
                this.$confirm("确认要删除该数据项吗?", "提示", {
                    confirmButtonText: "确定",
                    cancelButtonText: "取消",
                    type: "warning"
                }).then(() => {
                    this.list2 = this.list2.filter(item => {
                        let isD = true
                        this.selectlistRow4.forEach((itemr) => {
                            if (itemr.materielCode === item.materielCode) {
                                isD = false
                            }
                        })
                        return isD
                    })

                    this.$message({
                        type: "success",
                        message: "删除成功"
                    })
                }).catch(() => {
                    this.$message({
                        type: "info",
                        message: "已取消"
                    })
                })
            } else {
                this.$message.warning("请勾选记录！")
                return
            }
        },

        handleUpdate () {//编辑 
            this.resetTemp()
            if (this.selectlistRow) {
                if (this.selectlistRow.state !== "instruction20") {
                    this.Edit = true
                    this.temp = this.selectlistRow
                    this.listQueryItems.allocNumber = this.selectlistRow.allocNumber
                    api.getRecord(this.listQueryItems).then(response => {
                        response = response.data
                        this.list2 = response.list
                        this.total2 = response.pages.count
                    })

                    this.dialogFormVisible = true
                    this.dialogStatus = "update"
                } else {
                    this.$message({
                        title: "警告",
                        message: "发布状态下不可编辑",
                        type: "warning",
                        duration: 2000
                    })
                }

            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning",
                    duration: 2000
                })
            }
        },

        update () {//编辑
            this.temp.allocationItems = this.list2
            api.updateRecord(this.selectlistRow.allocNumber, this.temp).then(() => {
                this.dialogFormVisible = false
                this.$message({
                    title: "成功",
                    message: "编辑成功",
                    type: "success",
                    duration: 2000
                })
                this.getList()
                this.getItemsList()
            })
        },
        handleDblclickgysxz (row) { //父子页面通信
            this.parentsRows = row
        },
        handlezf () {//关闭
            if (this.selectlistRow) {
                api.operation(this.selectlistRow.allocNumber, 'CLOSE').then(() => {
                    this.$message({
                        title: "成功",
                        message: "作废成功",
                        type: "success",
                        duration: 2000
                    })
                    this.getList()
                })
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning",
                    duration: 2000
                })
            }
        },
        handlePublish () {//发布
            if (this.selectlistRow) {
                api.operation(this.selectlistRow.allocNumber, 'RELEASE').then(() => {
                    this.$message({
                        title: "成功",
                        message: "发布成功",
                        type: "success",
                        duration: 2000
                    })
                    this.getList()
                })
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning",
                    duration: 2000
                })
            }
        },
        selectRow (val) {//表一复选框选中方法
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
            if (this.selectlistRow) {
                this.getItemsList()
            }
        },
        selectRow1 (val) { //表二复选框选中方法
            this.selectlistRow1 = val
        },
        selectRow2 (val) {//表一单击行选中方法
            this.selectlistRow2 = val
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        },

        selectRow3 (val) {//表二单击行选中方法
            this.selectlistRow3 = val
        },
        selectRow4 (val) {
            this.selectlistRow4 = val
        },
        cancel () {
            this.dialogFormVisible = false
        },
        resetTemp () {
            this.temp = {
                allocNumber: undefined,
                remark: undefined,
                businessType: undefined,
                warehouseCode: undefined,
                toOrgCode: undefined,
                orgCode: undefined,
                toWarehouseCode: undefined,
            }
        },
        handleClose () {
            this.resetTemp()
            this.$refs.temp.resetFields()
        },
        handleSelectionChange (selection) {
            this.selectCode = selection.map(v => v.measureCode)
        }
    }
};
